# Definir cores para saída
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[0;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Diretório base do LightRAG
LIGHTRAG_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
echo -e "${BLUE}Diretório do LightRAG: ${LIGHTRAG_DIR}${NC}"

# Verificar se o diretório existe
if [ ! -d "${LIGHTRAG_DIR}" ]; then
    echo -e "${RED}Erro: Diretório LightRAG não encontrado${NC}"
    exit 1
fi

# Criar diretório de backup
BACKUP_DIR="${LIGHTRAG_DIR}/backups/$(date +%Y%m%d_%H%M%S)"
mkdir -p "${BACKUP_DIR}"
echo -e "${BLUE}Criando diretório de backup: ${BACKUP_DIR}${NC}"

# Fazer backup dos arquivos originais
echo -e "${YELLOW}Fazendo backup dos arquivos originais...${NC}"
cp -f "${LIGHTRAG_DIR}/utils/formatters.py" "${BACKUP_DIR}/formatters.py.bak"
cp -f "${LIGHTRAG_DIR}/core/database.py" "${BACKUP_DIR}/database.py.bak"
cp -f "${LIGHTRAG_DIR}/custom_project_names.json" "${BACKUP_DIR}/custom_project_names.json.bak" 2>/dev/null || :
cp -f "${LIGHTRAG_DIR}/lightrag_db.json" "${BACKUP_DIR}/lightrag_db.json.bak" 2>/dev/null || :

echo -e "${GREEN}Backup concluído em: ${BACKUP_DIR}${NC}"

# Configurar permissões dos novos scripts
echo -e "${YELLOW}Configurando permissões...${NC}"
chmod +x "${LIGHTRAG_DIR}/fixed_id_generator.py"
chmod +x "${LIGHTRAG_DIR}/update_custom_names.py"
chmod +x "${LIGHTRAG_DIR}/correct_duplicate_ids.py"
chmod +x "${LIGHTRAG_DIR}/migrate_database.py"

# Atualizar arquivos do sistema
echo -e "${YELLOW}Instalando arquivos atualizados...${NC}"

# Atualizar formatters.py
cp -f "${LIGHTRAG_DIR}/utils/formatters_updated.py" "${LIGHTRAG_DIR}/utils/formatters.py"
echo -e "${GREEN}✓ Formatador atualizado${NC}"

# Atualizar database.py
cp -f "${LIGHTRAG_DIR}/core/database_updated.py" "${LIGHTRAG_DIR}/core/database.py"
echo -e "${GREEN}✓ Banco de dados atualizado${NC}"

# Executar scripts de migração
echo -e "\n${BLUE}==== Iniciando migração do banco de dados ====${NC}"
echo -e "${YELLOW}Esta operação converterá os IDs para o formato consistente.${NC}"
