    echo -e "  ${YELLOW}cd ${LIGHTRAG_DIR} && python3 migrate_database.py${NC}"
    exit 0
fi

# Migrar banco de dados
echo -e "${YELLOW}Migrando banco de dados...${NC}"
cd "${LIGHTRAG_DIR}" && python3 migrate_database.py

# Atualizar mapeamentos
echo -e "${YELLOW}Atualizando mapeamentos de nomes...${NC}"
cd "${LIGHTRAG_DIR}" && python3 update_custom_names.py

# Corrigir duplicatas
echo -e "${YELLOW}Verificando e corrigindo IDs duplicados...${NC}"
cd "${LIGHTRAG_DIR}" && python3 correct_duplicate_ids.py

