# LightRAG - Script de instalação do sistema de nomes baseado em arquivos
# Este script instala e configura o novo sistema de mapeamento de nomes 
# que vincula nomes personalizados diretamente aos arquivos .jsonl

# Cores para saída
RED="\033[0;31m"
GREEN="\033[0;32m"
YELLOW="\033[0;33m"
BLUE="\033[0;34m"
RESET="\033[0m"

# Diretório base do LightRAG
LIGHTRAG_DIR="/Users/agents/.claude/lightrag"
UTILS_DIR="$LIGHTRAG_DIR/utils"
CORE_DIR="$LIGHTRAG_DIR/core"

# Arquivos de mapeamento
OLD_NAMES_FILE="$LIGHTRAG_DIR/custom_project_names.json"
NEW_NAMES_FILE="$LIGHTRAG_DIR/file_custom_names.json"

# Função para mostrar mensagens
show_message() {
    echo -e "${BLUE}$1${RESET}"
}

# Função para mostrar sucesso
show_success() {
    echo -e "${GREEN}✓ $1${RESET}"
}

# Função para mostrar erro
show_error() {
    echo -e "${RED}✗ $1${RESET}"
    exit 1
}

# Função para mostrar alerta
show_warning() {
    echo -e "${YELLOW}! $1${RESET}"
}

# Criar diretório de backups
create_backup_dir() {
    BACKUP_DIR="$LIGHTRAG_DIR/backups/$(date +%Y%m%d_%H%M%S)"
    mkdir -p "$BACKUP_DIR"
    show_success "Diretório de backups criado: $BACKUP_DIR"
}

# Fazer backup de arquivos
backup_files() {
    # Backup do arquivo de mapeamento antigo
    if [ -f "$OLD_NAMES_FILE" ]; then
        cp "$OLD_NAMES_FILE" "$BACKUP_DIR/"
        show_success "Backup de custom_project_names.json criado"
    fi

    # Backup de outros arquivos relevantes
    if [ -f "$UTILS_DIR/formatters.py" ]; then
        cp "$UTILS_DIR/formatters.py" "$BACKUP_DIR/"
        show_success "Backup de formatters.py criado"
    fi

    if [ -f "$CORE_DIR/database.py" ]; then
        cp "$CORE_DIR/database.py" "$BACKUP_DIR/"
        show_success "Backup de database.py criado"
    fi
}

# Verificar permissões dos scripts
check_permissions() {
    chmod +x "$LIGHTRAG_DIR/file_based_names.py"
    chmod +x "$LIGHTRAG_DIR/update_names.py"
    chmod +x "$LIGHTRAG_DIR/name_display.py"
    show_success "Permissões dos scripts atualizadas"
}

# Executar migração de nomes
run_migration() {
    show_message "Iniciando migração de nomes personalizados..."

