    if [ -f "$UTILS_DIR/formatters.py" ]; then
        cp "$UTILS_DIR/formatters.py" "$BACKUP_DIR/"
        show_success "Backup de formatters.py criado"
    fi

    if [ -f "$CORE_DIR/database.py" ]; then
        cp "$CORE_DIR/database.py" "$BACKUP_DIR/"
        show_success "Backup de database.py criado"
    fi
}

# Verificar permissões dos scripts
check_permissions() {
    chmod +x "$LIGHTRAG_DIR/file_based_names.py"
    chmod +x "$LIGHTRAG_DIR/update_names.py"
    chmod +x "$LIGHTRAG_DIR/name_display.py"
    show_success "Permissões dos scripts atualizadas"
}

