    echo
    echo "  python3 update_names.py"
    echo "      - Atualiza o mapeamento de nomes para todos os arquivos"
    echo

    show_message "O arquivo de mapeamento está em:"
    show_message "  $NEW_NAMES_FILE"
    echo
}

