create_backup_dir() {
    BACKUP_DIR="$LIGHTRAG_DIR/backups/ui_$(date +%Y%m%d_%H%M%S)"
    mkdir -p "$BACKUP_DIR"
    show_success "Diretório de backups criado: $BACKUP_DIR"
}

# Verificar permissões dos scripts
check_permissions() {
    chmod +x "$LIGHTRAG_DIR/file_based_names.py"
    chmod +x "$LIGHTRAG_DIR/update_names.py"
    chmod +x "$LIGHTRAG_DIR/ui_patch.py"
    show_success "Permissões dos scripts atualizadas"
}

# Copiar novos arquivos para a pasta UI
copy_display_names_module() {
    # Verificar se o arquivo existe
    if [ ! -f "$LIGHTRAG_DIR/ui/display_names.py" ]; then
        show_error "Arquivo display_names.py não encontrado"
    fi

    # Tornar executável
    chmod +x "$LIGHTRAG_DIR/ui/display_names.py"
    show_success "Módulo display_names.py instalado com sucesso"
}

# Aplicar patch na UI
apply_ui_patch() {
    show_message "Aplicando modificações na interface..."

    # Executar script de patch
    python3 "$LIGHTRAG_DIR/ui_patch.py"

    if [ $? -eq 0 ]; then
        show_success "Patch aplicado com sucesso"
    else
        show_error "Erro ao aplicar o patch"
    fi
}

# Migrar nomes personalizados
migrate_custom_names() {
    show_message "Migrando nomes personalizados..."

    # Executar script de atualização de nomes
    python3 "$LIGHTRAG_DIR/update_names.py"

    show_success "Nomes personalizados migrados com sucesso"
}

# Função principal
main() {
    echo
    echo "======================================================"
    echo "  INSTALAÇÃO DAS MODIFICAÇÕES DE UI DO LIGHTRAG       "
    echo "======================================================"
    echo

    show_message "Este script instalará modificações na interface do LightRAG"
    show_message "para ocultar IDs e mostrar apenas nomes personalizados."
    echo

    # Verificar se estamos no diretório correto
    if [ ! -d "$LIGHTRAG_DIR" ]; then
        show_error "Diretório do LightRAG não encontrado em $LIGHTRAG_DIR"
    fi

    # Criar diretório de backups
    create_backup_dir

    # Verificar permissões dos scripts
    check_permissions

    # Copiar módulo display_names para a pasta UI
    copy_display_names_module

    # Aplicar patch na UI
    apply_ui_patch

    # Migrar nomes personalizados
    migrate_custom_names

    echo
    show_message "A instalação foi concluída com sucesso!"
    echo
    show_message "Agora a interface do LightRAG mostrará apenas nomes personalizados"
    show_message "em vez de IDs em todos os lugares."
    echo
    show_message "Para definir nomes personalizados, use o comando:"
    echo "  python3 file_based_names.py set <arquivo.jsonl> \"Nome Personalizado\""
    echo
    show_message "Para atualizar os nomes, use:"
    echo "  python3 update_names.py"
    echo

    show_message "Os backups dos arquivos originais estão em:"
    show_message "  $BACKUP_DIR"
    echo
}

