# LightRAG - Script de instalação do sistema de nomes por UUID
# Este script instala o sistema robusto de nomes por UUID que garante
# que os nomes personalizados persistam mesmo quando os IDs mudarem

# Cores para saída
RED="\033[0;31m"
GREEN="\033[0;32m"
YELLOW="\033[0;33m"
BLUE="\033[0;34m"
RESET="\033[0m"

# Diretório base do LightRAG
LIGHTRAG_DIR="/Users/agents/.claude/lightrag"
BACKUP_DIR="$LIGHTRAG_DIR/backups/uuid_install_$(date +%Y%m%d_%H%M%S)"

# Função para mostrar mensagens
show_message() {
    echo -e "${BLUE}$1${RESET}"
}

# Função para mostrar sucesso
show_success() {
    echo -e "${GREEN}✓ $1${RESET}"
}

# Função para mostrar erro
show_error() {
    echo -e "${RED}✗ $1${RESET}"
    exit 1
}

# Função para mostrar alerta
show_warning() {
    echo -e "${YELLOW}! $1${RESET}"
}

# Criar diretório de backups
create_backup_dir() {
    mkdir -p "$BACKUP_DIR"
    show_success "Diretório de backups criado: $BACKUP_DIR"
}

# Verificar se os scripts existem
check_files() {
    files=(
        "$LIGHTRAG_DIR/uuid_names.py"
        "$LIGHTRAG_DIR/migrate_to_uuid.py"
        "$LIGHTRAG_DIR/database_patch.py"
    )

    for file in "${files[@]}"; do
        if [ ! -f "$file" ]; then
            show_error "Arquivo não encontrado: $file"
        fi
    done

    show_success "Todos os arquivos necessários foram encontrados"
}

# Verificar permissões dos scripts
check_permissions() {
    chmod +x "$LIGHTRAG_DIR/uuid_names.py"
    chmod +x "$LIGHTRAG_DIR/migrate_to_uuid.py"
    chmod +x "$LIGHTRAG_DIR/database_patch.py"
    show_success "Permissões dos scripts atualizadas"
}

# Fazer backup de arquivos importantes
backup_important_files() {
    files=(
        "$LIGHTRAG_DIR/custom_project_names.json"
        "$LIGHTRAG_DIR/core/database.py"
    )

    for file in "${files[@]}"; do
        if [ -f "$file" ]; then
            filename=$(basename "$file")
            cp "$file" "$BACKUP_DIR/$filename"
            show_success "Backup criado: $BACKUP_DIR/$filename"
        fi
    done
}

# Executar migração para o sistema de nomes por UUID
run_migration() {
    show_message "Migrando para o sistema de nomes por UUID..."
    python3 "$LIGHTRAG_DIR/migrate_to_uuid.py"

    if [ -f "$LIGHTRAG_DIR/uuid_names.json" ]; then
        show_success "Migração concluída com sucesso"
    else
        show_error "Falha na migração para o sistema de nomes por UUID"
    fi
}

# Aplicar patch no módulo de banco de dados
apply_database_patch() {
    show_message "Aplicando patch no módulo de banco de dados..."
    python3 "$LIGHTRAG_DIR/database_patch.py"

    # Verificar se o patch foi aplicado com sucesso
    grep -q "uuid_names" "$LIGHTRAG_DIR/core/database.py"
    if [ $? -eq 0 ]; then
        show_success "Patch aplicado com sucesso"
    else
        show_error "Falha ao aplicar o patch no módulo de banco de dados"
    fi
}

# Função principal
main() {
    echo
    echo "======================================================"
    echo "  INSTALAÇÃO DO SISTEMA DE NOMES POR UUID             "
    echo "======================================================"
    echo

    show_message "Este script instalará um sistema robusto de nomes por UUID"
    show_message "que garante que os nomes personalizados persistam mesmo"
    show_message "quando os IDs forem alterados."
    echo

    # Verificar se estamos no diretório correto
    if [ ! -d "$LIGHTRAG_DIR" ]; then
        show_error "Diretório do LightRAG não encontrado em $LIGHTRAG_DIR"
    fi

    # Criar diretório de backups
    create_backup_dir

    # Verificar se os scripts existem
    check_files

    # Verificar permissões dos scripts
    check_permissions

    # Fazer backup de arquivos importantes
    backup_important_files

    # Executar migração para o sistema de nomes por UUID
    run_migration

    # Aplicar patch no módulo de banco de dados
    apply_database_patch

    echo
    show_message "A instalação foi concluída com sucesso!"
    echo
    show_message "Agora os nomes personalizados persistirão mesmo quando os IDs mudarem,"
    show_message "garantindo consistência total na interface do LightRAG."
    echo
    show_message "Para gerenciar nomes personalizados, use o comando:"
    echo "  python3 uuid_names.py <comando>"
    echo
    show_message "Comandos disponíveis:"
    echo "  python3 uuid_names.py list"
    echo "      - Lista todos os nomes personalizados registrados"
    echo
    echo "  python3 uuid_names.py set <arquivo.jsonl> <nome>"
    echo "      - Define um nome personalizado para um arquivo"
    echo
    echo "  python3 uuid_names.py get <arquivo.jsonl>"
    echo "      - Obtém o nome personalizado de um arquivo"
    echo
    echo "  python3 uuid_names.py update"
    echo "      - Atualiza o sistema com todos os UUIDs encontrados"
    echo

    show_message "Os backups dos arquivos originais estão em:"
    show_message "  $BACKUP_DIR"
    echo
}

