{"parentUuid":"43a908d3-a63c-4e88-837a-2a89a17d1d62","isSidechain":false,"userType":"external","cwd":"/Users/agents/.claude/lightrag","sessionId":"8b225707-6263-4081-bc38-df505a930293","version":"0.2.124","message":{"id":"msg_013uxaccJ8MUbocbAaR6pB7P","type":"message","role":"assistant","model":"claude-3-7-sonnet-20250219","content":[{"type":"text","text":"Agora, vamos criar um script de instalação final que aplicará todas as modificações:"},{"type":"tool_use","id":"toolu_01F592ZKn1yHjoYXTCLnsX7c","name":"Write","input":{"file_path":"/Users/agents/.claude/lightrag/install_naming_ui.sh","content":"#!/bin/bash

# LightRAG - Script de instalação das modificações de UI para nomeação
# Este script instala todas as modificações necessárias para permitir
# que o usuário nomeie conversas diretamente na interface do LightRAG

# Cores para saída
RED="\033[0;31m"
GREEN="\033[0;32m"
YELLOW="\033[0;33m"
BLUE="\033[0;34m"
RESET="\033[0m"

# Diretório base do LightRAG
LIGHTRAG_DIR="/Users/agents/.claude/lightrag"
UI_DIR="$LIGHTRAG_DIR/ui"

# Função para mostrar mensagens
show_message() {
    echo -e "${BLUE}$1${RESET}"
}

# Função para mostrar sucesso
show_success() {
    echo -e "${GREEN}✓ $1${RESET}"
}

# Função para mostrar erro
show_error() {
    echo -e "${RED}✗ $1${RESET}"
    exit 1
}

# Função para mostrar alerta
show_warning() {
    echo -e "${YELLOW}! $1${RESET}"
}

# Criar diretório de backups
create_backup_dir() {
    BACKUP_DIR="$LIGHTRAG_DIR/backups/naming_ui_$(date +%Y%m%d_%H%M%S)"
    mkdir -p "$BACKUP_DIR"
    show_success "Diretório de backups criado: $BACKUP_DIR"
}

# Verificar permissões dos scripts
check_permissions() {
    chmod +x "$LIGHTRAG_DIR/uuid_names.py"
    chmod +x "$LIGHTRAG_DIR/ui_name_patch.py"
    chmod +x "$LIGHTRAG_DIR/ui/name_selection.py"
    show_success "Permissões dos scripts atualizadas"
}

# Garantir que o sistema de nomes por UUID esteja instalado
ensure_uuid_names() {
    if [ ! -f "$LIGHTRAG_DIR/uuid_names.py" ]; then
        show_error "Arquivo uuid_names.py não encontrado. Execute install_uuid_names.sh primeiro."
    fi

    # Verificar se o arquivo de mapeamento existe
    if [ ! -f "$LIGHTRAG_DIR/uuid_names.json" ]; then
        show_message "Criando arquivo de mapeamento de nomes..."
        python3 "$LIGHTRAG_DIR/uuid_names.py" update
    fi

    show_success "Sistema de nomes por UUID verificado"
}

# Copiar módulo de nomeação para a pasta UI
install_naming_module() {
    # Verificar se o arquivo existe
    if [ ! -f "$LIGHTRAG_DIR/ui/name_selection.py" ]; then
        show_error "Arquivo name_selection.py não encontrado"
    fi

    show_success "Módulo de nomeação verificado"
}

# Aplicar patch na UI
apply_ui_patch() {
    show_message "Aplicando modificações na interface..."

    # Executar script de patch
    python3 "$LIGHTRAG_DIR/ui_name_patch.py"

    if [ $? -eq 0 ]; then
        show_success "Modificações aplicadas com sucesso"
    else
        show_error "Erro ao aplicar modificações na interface"
    fi
}

# Reiniciar servidor Streamlit
restart_server() {
    show_message "Tentando reiniciar o servidor Streamlit..."

    # Verificar se há processos Streamlit rodando
    STREAMLIT_PID=$(ps aux | grep "streamlit run" | grep -v grep | awk '{print $2}')

    if [ -n "$STREAMLIT_PID" ]; then
        # Tentar matar o processo
        kill -15 $STREAMLIT_PID 2>/dev/null
        sleep 1

        # Verificar se o processo ainda existe
        if ps -p $STREAMLIT_PID > /dev/null; then
            show_warning "Não foi possível reiniciar o servidor automaticamente."
            show_message "Por favor, reinicie o servidor manualmente para aplicar as modificações:"
            show_message "  1. Pare o servidor atual"
            show_message "  2. Execute: cd $LIGHTRAG_DIR && ./start_ui.sh"
        else
            show_success "Servidor Streamlit reiniciado"
        fi
    else
        show_message "Nenhum servidor Streamlit encontrado em execução."
    fi
}

# Função principal
main() {
    echo
    echo "======================================================"
    echo "  INSTALAÇÃO DA UI DE NOMEAÇÃO DO LIGHTRAG            "
    echo "======================================================"
    echo

    show_message "Este script instalará modificações na interface do LightRAG"
    show_message "para permitir que o usuário nomeie conversas diretamente"
    show_message "durante a seleção de documentos."
    echo

    # Verificar se estamos no diretório correto
    if [ ! -d "$LIGHTRAG_DIR" ]; then
        show_error "Diretório do LightRAG não encontrado em $LIGHTRAG_DIR"
    fi

    # Criar diretório de backups
    create_backup_dir

    # Verificar permissões dos scripts
    check_permissions

    # Garantir que o sistema de nomes por UUID esteja instalado
    ensure_uuid_names

    # Instalar módulo de nomeação
    install_naming_module

    # Aplicar patch na UI
    apply_ui_patch

    # Tentar reiniciar servidor
    restart_server

    echo
    show_message "A instalação foi concluída com sucesso!"
    echo
    show_message "Agora você pode nomear conversas diretamente na interface do LightRAG:"
    echo
    show_message "1. Ao selecionar uma conversa sem nome personalizado,"
    show_message "   você verá um campo para definir um nome mais descritivo."
    echo
    show_message "2. Digite o nome desejado e clique em 'Salvar nome'."
    echo
    show_message "3. O nome será salvo e utilizado em todas as partes da interface."
    echo

    show_message "Os backups dos arquivos originais estão em:"
    show_message "  $BACKUP_DIR"
    echo
}

