   151	    def extract_entities(self, text):
   152	        """
   153	        Extrai entidades mencionadas em um texto (menções a Memory MCP)
   154	        
   155	        Args:
   156	            text: Texto para análise
   157	            
   158	        Retorna:
   159	            list: Lista de entidades encontradas
   160	        """
   161	        logger.debug("Extraindo entidades do texto")
   162	        # Expressão regular para encontrar entidades em formato JSON
   163	        entity_pattern = r'"name"\s*:\s*"([^"]+)"'
   164	        relation_patterns = [
   165	            r'"from"\s*:\s*"([^"]+)"',
   166	            r'"to"\s*:\s*"([^"]+)"'
   167	        ]
   168	        
   169	        entities = set()
   170	        
   171	        # Encontrar entidades diretas
   172	        for match in re.finditer(entity_pattern, text):
   173	            entities.add(match.group(1))
   174	        
   175	        # Encontrar entidades em relações
   176	        for pattern in relation_patterns:
   177	            for match in re.finditer(pattern, text):
   178	                entities.add(match.group(1))
   179	        
   180	        return list(entities)
