   127	    def extract_entities(self, text):
   128	        """
   129	        Extrai entidades mencionadas em um texto (menções a Memory MCP)
   130	        
   131	        Args:
   132	            text: Texto para análise
   133	            
   134	        Retorna:
   135	            list: Lista de entidades encontradas
   136	        """
   137	        logger.debug("Extraindo entidades do texto")
   138	        # Expressão regular para encontrar entidades em formato JSON
   139	        entity_pattern = r'"name"\s*:\s*"([^"]+)"'
   140	        relation_patterns = [
   141	            r'"from"\s*:\s*"([^"]+)"',
   142	            r'"to"\s*:\s*"([^"]+)"'
   143	        ]
   144	        
   145	        entities = set()
   146	        
   147	        # Encontrar entidades diretas
   148	        for match in re.finditer(entity_pattern, text):
   149	            entities.add(match.group(1))
   150	        
   151	        # Encontrar entidades em relações
   152	        for pattern in relation_patterns:
   153	            for match in re.finditer(pattern, text):
   154	                entities.add(match.group(1))
   155	        
   156	        return list(entities)
