Módulo unificado para integrar o LightRAG com o serviço Model Context Protocol (MCP) do Claude","originalFile":"#!/usr/bin/env python3
# -*- coding: utf-8 -*-

"""
LightRAG MCP Client
Módulo unificado para integrar o LightRAG com o serviço MCP do Claude
"""

import hashlib
import os
from typing import Dict, List, Any, Optional, Union

# Importar componentes do LightRAG
from core.client import LightRAGClient, ensure_server_running
from utils.logger import get_core_logger

# Configurar logger
logger = get_core_logger()

# Função para MCP do Claude
def rag_query(query, mode="hybrid", max_results=5):
    """
    Função de consulta para uso via MCP

    Args:
        query: Texto da consulta
        mode: Modo de consulta (hybrid, semantic, keyword)
        max_results: Número máximo de resultados

    Retorna:
        Dict: Resultados da consulta
    """
    logger.info(f"MCP: rag_query chamado com '{query}'")

    # Garantir que o servidor esteja rodando
    if not ensure_server_running():
        error_msg = "Não foi possível garantir que o servidor LightRAG esteja rodando."
        logger.error(error_msg)
        return {"error": error_msg, "context": [], "response": error_msg}

    # Realizar consulta
    client = LightRAGClient()
    result = client.query(query, max_results, mode)

    if "error" not in result:
        logger.info(f"MCP: consulta bem-sucedida com {len(result.get('context', []))} resultados")
    else:
        logger.error(f"MCP: erro na consulta - {result.get('error')}")

    return result

# Função para MCP do Claude
def rag_insert_text(text, source="mcp", summary=None, metadata=None):
    """
    Função de inserção para uso via MCP

    Args:
        text: Texto a ser inserido
        source: Identificador da fonte do texto
        summary: Resumo opcional do documento
        metadata: Metadados adicionais

    Retorna:
        Dict: Resultado da operação
    """
    logger.info(f"MCP: rag_insert_text chamado (fonte={source}, tamanho={len(text)})")

    # Garantir que o servidor esteja rodando
    if not ensure_server_running():
        error_msg = "Não foi possível garantir que o servidor LightRAG esteja rodando."
        logger.error(error_msg)
        return {"success": False, "error": error_msg}

    # Preparar metadados
    if metadata is None:
        metadata = {}

    # Adicionar hash do conteúdo para verificação de duplicidade
    content_hash = hashlib.sha256(text.encode('utf-8')).hexdigest()
    metadata["content_hash"] = content_hash

    # Inserir texto
    client = LightRAGClient()
    result = client.insert(text, summary, source, metadata)

    if result.get("success", False):
        logger.info(f"MCP: texto inserido com ID {result.get('documentId')}")
    else:
