    else:
        print("Não foi possível garantir que o servidor LightRAG esteja rodando.")","structuredPatch":[{"oldStart":20,"oldLines":7,"newStart":20,"newLines":7,"lines":[" # Função para Model Context Protocol (MCP) do Claude"," def rag_query(query, mode="hybrid", max_results=5):","     """","-    Função de consulta para uso via MCP","+    Função de consulta para uso via Model Context Protocol (MCP)","     ","     Args:","         query: Texto da consulta"]},{"oldStart":52,"oldLines":7,"newStart":52,"newLines":7,"lines":[" # Função para Model Context Protocol (MCP) do Claude"," def rag_insert_text(text, source="mcp", summary=None, metadata=None):","     """","-    Função de inserção para uso via MCP","+    Função de inserção para uso via Model Context Protocol (MCP)","     ","     Args:","         text: Texto a ser inserido"]},{"oldStart":93,"oldLines":7,"newStart":93,"newLines":7,"lines":[" # Função para Model Context Protocol (MCP) do Claude para inserção de arquivo"," def rag_insert_file(file_path, source="file", force=False, max_lines=500):","     """","-    Função de inserção de arquivo para uso via MCP","+    Função de inserção de arquivo para uso via Model Context Protocol (MCP)","     ","     Args:","         file_path: Caminho do arquivo a ser inserido"]},{"oldStart":190,"oldLines":7,"newStart":190,"newLines":7,"lines":[" # Função para Model Context Protocol (MCP) do Claude"," def rag_status():","     """","-    Função de status para uso via MCP","+    Função de status para uso via Model Context Protocol (MCP)","     ","     Retorna:","         Dict: Informações de status"]},{"oldStart":210,"oldLines":7,"newStart":210,"newLines":7,"lines":[" # Função para Model Context Protocol (MCP) do Claude"," def rag_clear(confirm=True):","     """","-    Função para limpar base para uso via MCP","+    Função para limpar base para uso via Model Context Protocol (MCP)","     ","     Args:","         confirm: Confirmação explícita"]}]}}
