                                    time.sleep(0.5)
                                    st.rerun()
                                else:
                                    st.error("Falha ao salvar o nome personalizado.")

                            # Mostrar o botão para limpar o nome
                            if custom_name and st.button("🗑️ Limpar Nome", key=f"clear_name_{doc_id}"):
                                save_custom_name(doc_id, "")
