#!/usr/bin/env python3
# -*- coding: utf-8 -*-

"""
LightRAG - Sistema simples de RAG (Retrieval Augmented Generation)
Implementação minimalista para uso com Claude via MCP
"""

from flask import Flask, request, jsonify
import json
import os
import datetime
import re

# Configuração
app = Flask(__name__)
DB_FILE = os.path.join(os.path.dirname(os.path.abspath(__file__)), 'lightrag_db.json')
MAX_RESULTS = 5  # Número máximo de resultados retornados

# Base de conhecimento
knowledge_base = {
    "documents": [],
    "lastUpdated": datetime.datetime.now().isoformat()
}

# Carregar base de conhecimento se existir
if os.path.exists(DB_FILE):
    try:
        with open(DB_FILE, 'r', encoding='utf-8') as f:
            knowledge_base = json.load(f)
        print(f"Base de conhecimento carregada com {len(knowledge_base['documents'])} documentos")
    except Exception as e:
        print(f"Erro ao carregar base de conhecimento: {str(e)}")

# Salvar base de conhecimento
def save_knowledge_base():
    try:
        with open(DB_FILE, 'w', encoding='utf-8') as f:
            json.dump(knowledge_base, f, indent=2, ensure_ascii=False)
        return True
    except Exception as e:
        print(f"Erro ao salvar base de conhecimento: {str(e)}")
        return False

# Função para calcular relevância entre consulta e documento
def calculate_relevance(query, content):
    query_lower = query.lower()
    content_lower = content.lower()

    # Verificar correspondência exata
    if query_lower in content_lower:
        return 0.9  # Alta relevância para correspondência exata

    # Verificar palavras individuais
    query_words = set(re.findall(r'\w+', query_lower))
    content_words = set(re.findall(r'\w+', content_lower))

    if not query_words:
        return 0

    # Calcular interseção de palavras
    common_words = query_words.intersection(content_words)
    if not common_words:
        return 0

    # Calcular relevância pela proporção de palavras correspondentes
    return len(common_words) / len(query_words) * 0.8

# Endpoint de status
@app.route('/status', methods=['GET'])
def status():
    return jsonify({
        "status": "online",
        "documents": len(knowledge_base["documents"]),
        "lastUpdated": knowledge_base["lastUpdated"]
    })

# Endpoint de consulta
@app.route('/query', methods=['POST', 'OPTIONS'])
def query():
    # Lidar com CORS preflight requests
    if request.method == 'OPTIONS':
        return "", 204

    # Processar consulta
    try:
        data = request.json
        query_text = data.get('query', '')
        max_results = data.get('max_results', MAX_RESULTS)

        print(f"Consulta recebida: {query_text}")

        if not query_text:
            return jsonify({
                "success": False,
                "error": "Consulta vazia"
            }), 400

        # Resposta padrão
        response = {
            "response": f'Resposta para: "{query_text}"',
            "context": []
        }

        # Buscar documentos relevantes
        if knowledge_base["documents"]:
            # Calcular relevância de cada documento
            docs_with_relevance = [
                {"doc": doc, "relevance": calculate_relevance(query_text, doc["content"])}
                for doc in knowledge_base["documents"]
            ]

            # Filtrar documentos com alguma relevância
            relevant_docs = [
                item for item in docs_with_relevance 
                if item["relevance"] > 0
            ]

            # Ordenar por relevância e limitar resultados
            relevant_docs.sort(key=lambda x: x["relevance"], reverse=True)
            top_docs = relevant_docs[:max_results]

            if top_docs:
                # Adicionar contextos encontrados
                response["context"] = [
                    {
                        "content": doc["doc"]["content"],
                        "source": doc["doc"].get("source", "desconhecido"),
                        "document_id": doc["doc"].get("id", "doc_desconhecido"),
                        "relevance": doc["relevance"]
                    } for doc in top_docs
                ]

                response["response"] = f'Com base no conhecimento disponível, aqui está a resposta para: "{query_text}"'

        return jsonify(response)

    except Exception as e:
        print(f"Erro ao processar consulta: {e}")
        return jsonify({"error": str(e)}), 500

# Endpoint de inserção
@app.route('/insert', methods=['POST', 'OPTIONS'])
def insert():
    # Lidar com CORS preflight requests
    if request.method == 'OPTIONS':
        return "", 204

    # Processar inserção
    try:
        data = request.json
        text = data.get('text', '')
        source = data.get('source', 'manual')
        summary = data.get('summary', 'Documento manual')

        if not text:
            return jsonify({
                "success": False,
                "error": "Texto para inserção não fornecido"
            }), 400

        # Adicionar documento
        doc_id = f"doc_{int(datetime.datetime.now().timestamp() * 1000)}"
        knowledge_base["documents"].append({
            "id": doc_id,
            "content": text,
            "source": source,
            "summary": summary,
            "created": datetime.datetime.now().isoformat()
        })

        # Atualizar timestamp
        knowledge_base["lastUpdated"] = datetime.datetime.now().isoformat()

        # Salvar base de conhecimento
        if save_knowledge_base():
            print(f"Documento inserido: {text[:50]}...")
            return jsonify({
                "success": True,
                "message": "Documento inserido com sucesso",
                "documentId": doc_id
            })
        else:
            return jsonify({
                "success": False,
                "error": "Erro ao salvar base de conhecimento"
            }), 500

    except Exception as e:
        print(f"Erro ao processar inserção: {e}")
        return jsonify({"error": str(e)}), 500

# Endpoint para remover documento específico
@app.route('/delete', methods=['POST', 'OPTIONS'])
def delete_document():
    # Lidar com CORS preflight requests
    if request.method == 'OPTIONS':
        return "", 204

    # Processar exclusão
    try:
        data = request.json
        doc_id = data.get('id', '')

        if not doc_id:
            return jsonify({
                "success": False,
                "error": "ID do documento não fornecido"
            }), 400

        # Verificar se o documento existe
        original_count = len(knowledge_base["documents"])
        documents_filtered = [doc for doc in knowledge_base["documents"] if doc.get("id") != doc_id]

        if len(documents_filtered) == original_count:
            return jsonify({
                "success": False,
                "error": f"Documento com ID '{doc_id}' não encontrado"
            }), 404

        # Atualizar base de conhecimento
        knowledge_base["documents"] = documents_filtered
        knowledge_base["lastUpdated"] = datetime.datetime.now().isoformat()

        # Salvar base de conhecimento
        if save_knowledge_base():
            print(f"Documento removido: {doc_id}")
            return jsonify({
                "success": True,
                "message": f"Documento '{doc_id}' removido com sucesso"
            })
        else:
            return jsonify({
                "success": False,
                "error": "Erro ao salvar base de conhecimento"
            }), 500

    except Exception as e:
        print(f"Erro ao processar exclusão: {e}")
        return jsonify({"error": str(e)}), 500

# Endpoint para limpar a base
@app.route('/clear', methods=['POST', 'OPTIONS'])
def clear():
    # Lidar com CORS preflight requests
    if request.method == 'OPTIONS':
        return "", 204

    # Processar limpeza
    try:
        data = request.json
        confirm = data.get('confirm', False)

        if not confirm:
            return jsonify({
                "success": False,
                "error": "Confirmação necessária para limpar a base de conhecimento"
            }), 400

        # Criar backup
        backup_file = f"{DB_FILE}.bak.{int(datetime.datetime.now().timestamp())}"
        try:
            with open(DB_FILE, 'r', encoding='utf-8') as src, open(backup_file, 'w', encoding='utf-8') as dst:
                dst.write(src.read())
        except Exception as e:
            print(f"Erro ao criar backup: {e}")

        # Limpar base
        doc_count = len(knowledge_base["documents"])
        knowledge_base["documents"] = []
        knowledge_base["lastUpdated"] = datetime.datetime.now().isoformat()

        # Salvar base vazia
        if save_knowledge_base():
            return jsonify({
                "success": True,
                "message": f"Base de conhecimento limpa. {doc_count} documentos removidos.",
                "backup": backup_file
            })
        else:
            return jsonify({
                "success": False,
                "error": "Erro ao salvar base de conhecimento vazia"
            }), 500

    except Exception as e:
        print(f"Erro ao limpar base de conhecimento: {e}")
        return jsonify({"error": str(e)}), 500

# Adicionar cabeçalhos CORS para todas as respostas
@app.after_request
def add_cors_headers(response):
    response.headers.add('Access-Control-Allow-Origin', '*')
    response.headers.add('Access-Control-Allow-Headers', 'Content-Type')
    response.headers.add('Access-Control-Allow-Methods', 'GET, POST, OPTIONS')
    return response

# Iniciar aplicação se for executado diretamente
if __name__ == '__main__':
    print(f"Servidor LightRAG Flask rodando em http://127.0.0.1:5000")
    print(f"Base de conhecimento: {DB_FILE} ({len(knowledge_base['documents'])} documentos)")
    print("Endpoints disponíveis:")
    print("  - GET  /status - Verifica o status do servidor")
    print("  - POST /query  - Consulta a base de conhecimento")
    print("  - POST /insert - Adiciona conteúdo à base")
    print("  - POST /delete - Remove um documento específico")
    print("  - POST /clear  - Limpa a base de conhecimento")
    app.run(host='127.0.0.1', port=5000)

#!/usr/bin/env python3
# -*- coding: utf-8 -*-

"""
LightRAG MCP Client
Módulo para integrar o LightRAG com o serviço MCP do Claude
"""

import json
import urllib.request
import urllib.parse
import time
import os
import traceback

class LightRAGClient:
    """Cliente Python para o servidor LightRAG"""

    def __init__(self, host="127.0.0.1", port=5000):
        self.base_url = f"http://{host}:{port}"
        self.last_error = None

    def check_server(self):
        """Verifica se o servidor está online"""
        try:
            result = self._make_request("status")
            return result is not None and result.get("status") == "online"
        except Exception:
            return False

    def _make_request(self, endpoint, data=None):
        """Faz uma requisição ao servidor LightRAG"""
        url = f"{self.base_url}/{endpoint}"
        headers = {"Content-Type": "application/json"}

        try:
            if data:
                # Requisição POST
                data_json = json.dumps(data).encode('utf-8')
                req = urllib.request.Request(url, data=data_json, headers=headers, method="POST")
            else:
                # Requisição GET
                req = urllib.request.Request(url, headers=headers, method="GET")

            with urllib.request.urlopen(req) as response:
                response_data = response.read().decode('utf-8')
                return json.loads(response_data)

        except Exception as e:
            self.last_error = str(e)
            return None

    def rag_query(self, query, mode="hybrid", max_results=5):
        """
        Consulta a base de conhecimento do LightRAG

        Parâmetros:
        - query: texto da consulta
        - mode: modo de consulta (padrão: "hybrid")
        - max_results: número máximo de resultados (padrão: 5)

        Retorna:
        - Dicionário com a resposta e contextos encontrados
        """
        if not query:
            return {"error": "Consulta vazia", "context": [], "response": "Consulta vazia"}

        data = {
            "query": query,
            "mode": mode,
            "max_results": max_results
        }

        result = self._make_request("query", data)

        if not result:
            return {
                "error": f"Falha na consulta: {self.last_error}",
                "context": [],
                "response": f"Erro ao conectar ao servidor LightRAG: {self.last_error}"
            }

        return result

    def rag_insert_text(self, text, source="mcp"):
        """
        Insere texto na base de conhecimento

        Parâmetros:
        - text: texto a ser inserido
        - source: identificador da fonte do texto

        Retorna:
        - Dicionário com status da operação
        """
        if not text:
            return {"success": False, "error": "Texto vazio"}

        data = {
            "text": text,
            "source": source
        }

        result = self._make_request("insert", data)

        if not result:
            return {"success": False, "error": f"Falha na inserção: {self.last_error}"}

        return result

    def clear_database(self, confirm=True):
        """
        Limpa toda a base de conhecimento (use com cuidado!)

        Parâmetros:
        - confirm: confirmação explícita (padrão: True)

        Retorna:
        - Dicionário com status da operação
        """
        data = {"confirm": confirm}
        result = self._make_request("clear", data)

        if not result:
            return {"success": False, "error": f"Falha ao limpar: {self.last_error}"}

        return result

    def get_status(self):
        """
        Obtém status do servidor e da base de conhecimento

        Retorna:
        - Dicionário com informações de status
        """
        result = self._make_request("status")

        if not result:
            return {"status": "offline", "error": self.last_error}

        return result

# Função para MCP do Claude
def rag_query(query, mode="hybrid", max_results=5):
    """Função de consulta para uso via MCP"""
    client = LightRAGClient()
    return client.rag_query(query, mode, max_results)

# Função para MCP do Claude
def rag_insert_text(text, source="mcp"):
    """Função de inserção para uso via MCP"""
    client = LightRAGClient()
    return client.rag_insert_text(text, source)

# Função para MCP do Claude
def rag_status():
    """Função de status para uso via MCP"""
    client = LightRAGClient()
    return client.get_status()

# Função para MCP do Claude
def rag_clear(confirm=True):
    """Função para limpar base para uso via MCP"""
    client = LightRAGClient()
    return client.clear_database(confirm)

# Função auxiliar para iniciar servidor caso não esteja rodando
def ensure_server_running():
    """Garante que o servidor esteja em execução"""
    client = LightRAGClient()
    if not client.check_server():
        print("Servidor LightRAG não está rodando. Tentando iniciar...")
        try:
            # Encontrar o diretório lightrag
            lightrag_dir = os.path.join(os.path.expanduser("~"), ".claude", "lightrag")
            start_script = os.path.join(lightrag_dir, "start_lightrag.sh")

            if os.path.exists(start_script):
                os.system(f"bash {start_script} > /dev/null 2>&1 &")
                time.sleep(3)  # Aguardar inicialização

                if client.check_server():
                    print("Servidor LightRAG iniciado com sucesso!")
                    return True
                else:
                    print("Falha ao iniciar servidor automaticamente.")
            else:
                print(f"Script de inicialização não encontrado: {start_script}")
        except Exception as e:
            print(f"Erro ao iniciar servidor: {e}")
            traceback.print_exc()

        return False
    return True

# Uso para teste
if __name__ == "__main__":
    # Garantir que o servidor esteja rodando
    if ensure_server_running():
        # Testar funcionalidades
        status = rag_status()
        print(f"Status do servidor: {status}")

        # Inserir um documento
        texto = "O LightRAG é um sistema de RAG simplificado para uso com Claude."
        insert_result = rag_insert_text(texto)
        print(f"Inserção: {insert_result}")

        # Fazer uma consulta
        query_result = rag_query("O que é LightRAG?")
        print(f"Consulta: {query_result}")
    else:
        print("Não foi possível garantir que o servidor LightRAG esteja rodando.")

#!/usr/bin/env python3
# -*- coding: utf-8 -*-

"""
LightRAG UI - Interface para o sistema LightRAG
Implementação com Streamlit para visualização e gerenciamento da base de conhecimento
"""

import streamlit as st
import json
import os
import requests
import datetime
import pandas as pd

# Configuração
LIGHTRAG_URL = "http://127.0.0.1:5000"
DB_FILE = os.path.join(os.path.dirname(os.path.abspath(__file__)), 'lightrag_db.json')

# Título e introdução
st.set_page_config(
    page_title="LightRAG - Interface", 
    page_icon="🔍", 
    layout="wide"
)

st.title("🔍 LightRAG - Sistema de RAG")
st.subheader("Retrieval Augmented Generation")

# Verificar conexão com o servidor
@st.cache_data(ttl=5)
def check_server():
    try:
        response = requests.get(f"{LIGHTRAG_URL}/status", timeout=2)
        if response.status_code == 200:
            return response.json()
        return {"status": "offline", "error": f"Código de resposta: {response.status_code}"}
    except Exception as e:
        return {"status": "offline", "error": str(e)}

# Carregar base de conhecimento
@st.cache_data(ttl=5)
def load_knowledge_base():
    if os.path.exists(DB_FILE):
        try:
            with open(DB_FILE, 'r', encoding='utf-8') as f:
                return json.load(f)
        except Exception as e:
            st.error(f"Erro ao carregar base de conhecimento: {str(e)}")
    return {"documents": [], "lastUpdated": ""}

# Função para excluir documento
def delete_document(doc_id):
    try:
        response = requests.post(
            f"{LIGHTRAG_URL}/delete", 
            json={"id": doc_id},
            timeout=5
        )
        if response.status_code == 200:
            result = response.json()
            if result.get("success"):
                st.success(f"Documento {doc_id} excluído com sucesso!")
                st.cache_data.clear()  # Limpar cache para atualizar a lista
                return True
            else:
                st.error(result.get("error", "Erro desconhecido"))
        else:
            st.error(f"Erro na exclusão: {response.status_code}")
    except Exception as e:
        st.error(f"Erro ao conectar ao servidor: {str(e)}")
    return False

# Verificar status do servidor
server_status = check_server()
status_color = "green" if server_status.get("status") == "online" else "red"

# Layout com colunas para status e pesquisa
col1, col2 = st.columns([1, 3])

with col1:
    st.markdown(f"### Status do Servidor: <span style='color:{status_color};'>●</span> {server_status.get('status', 'desconhecido')}", unsafe_allow_html=True)
    st.write(f"Documentos: {server_status.get('documents', 0)}")
    st.write(f"Última atualização: {server_status.get('lastUpdated', 'N/A')}")

with col2:
    # Campo de consulta
    query = st.text_input("Pesquisar na base de conhecimento:", placeholder="Digite sua consulta aqui...")
    if st.button("Consultar"):
        if query:
            try:
                response = requests.post(
                    f"{LIGHTRAG_URL}/query", 
                    json={"query": query, "max_results": 10},
                    timeout=5
                )
                if response.status_code == 200:
                    result = response.json()
                    st.success(result.get("response", "Sem resposta"))

                    # Exibir contextos encontrados
                    if result.get("context"):
                        st.markdown("### Documentos relevantes encontrados:")
                        for i, ctx in enumerate(result.get("context", [])):
                            with st.expander(f"Documento {i+1} - Relevância: {ctx.get('relevance', 0):.2f}"):
                                st.write(ctx.get("content", ""))
                                st.caption(f"Fonte: {ctx.get('source', 'desconhecido')}")
                                if "document_id" in ctx:
                                    st.caption(f"ID: {ctx.get('document_id', '')}")
                    else:
                        st.info("Nenhum documento relevante encontrado.")
                else:
                    st.error(f"Erro na consulta: {response.status_code}")
            except Exception as e:
                st.error(f"Erro ao conectar ao servidor: {str(e)}")
        else:
            st.warning("Por favor, digite uma consulta.")

# Abas para diferentes funcionalidades
tab1, tab2, tab3 = st.tabs(["Documentos", "Inserir", "Estatísticas"])

# Aba de visualização de documentos
with tab1:
    st.markdown("## Documentos na Base de Conhecimento")

    # Carregar base de conhecimento
    kb = load_knowledge_base()
    documents = kb.get("documents", [])

    if documents:
        # Exibir tabela de documentos
        docs_data = []
        for doc in documents:
            # Truncar conteúdo longo
            content = doc.get("content", "")
            if len(content) > 100:
                content = content[:97] + "..."

            docs_data.append({
                "ID": doc.get("id", ""),
                "Resumo": doc.get("summary", "Arquivo de histórico de conversa"),
                "Arquivo": content,
                "Criado": doc.get("created", "").split("T")[0]
            })

        # Exibir tabela de documentos
        df = pd.DataFrame(docs_data)
        st.dataframe(df, use_container_width=True)

        # Visualizar documento completo
        selected_doc_id = st.selectbox("Selecione um documento para visualizar:", 
                                      [""] + [doc.get("id", "") for doc in documents])

        if selected_doc_id:
            doc = next((d for d in documents if d.get("id") == selected_doc_id), None)
            if doc:
                col1, col2 = st.columns([4, 1])
                with col1:
                    st.subheader(doc.get("summary", "Documento"))
                    st.text_area("Conteúdo completo:", doc.get("content", ""), height=200)
                    st.write(f"Caminho: {doc.get('path', 'desconhecido')}")
                    st.write(f"Criado em: {doc.get('created', 'N/A')}")
                with col2:
                    st.write("### Ações do documento")
                    if st.button("🗑️ Excluir documento", type="primary", key=f"delete_{selected_doc_id}"):
                        if delete_document(selected_doc_id):
                            st.rerun()  # Recarregar a página para atualizar
    else:
        st.info("Nenhum documento encontrado na base de conhecimento.")

# Aba de inserção de novos documentos
with tab2:
    st.markdown("## Adicionar Novo Documento")

    # Formulário para adicionar documento
    with st.form("insert_form"):
        doc_content = st.text_area("Conteúdo do documento:", height=200)
        doc_source = st.text_input("Fonte:", "manual")
        doc_summary = st.text_input("Resumo do documento:", "Nota manual")

        submitted = st.form_submit_button("Inserir Documento")
        if submitted:
            if doc_content:
                try:
                    response = requests.post(
                        f"{LIGHTRAG_URL}/insert", 
                        json={"text": doc_content, "source": doc_source, "summary": doc_summary},
                        timeout=5
                    )
                    if response.status_code == 200:
                        result = response.json()
                        if result.get("success"):
                            st.success(f"Documento inserido com sucesso! ID: {result.get('documentId')}")
                            st.cache_data.clear()  # Limpar cache para atualizar a lista
                        else:
                            st.error(result.get("error", "Erro desconhecido"))
                    else:
                        st.error(f"Erro na inserção: {response.status_code}")
                except Exception as e:
                    st.error(f"Erro ao conectar ao servidor: {str(e)}")
            else:
                st.warning("Por favor, digite o conteúdo do documento.")

    # Opção para limpar base
    st.markdown("## Gerenciamento da Base")
    if st.button("Limpar toda a base de conhecimento", type="primary", use_container_width=True):
        try:
            response = requests.post(
                f"{LIGHTRAG_URL}/clear", 
                json={"confirm": True},
                timeout=5
            )
            if response.status_code == 200:
                result = response.json()
                if result.get("success"):
                    st.success(result.get("message", "Base limpa com sucesso"))
                    st.info(f"Backup criado: {result.get('backup', 'N/A')}")
                    st.cache_data.clear()  # Limpar cache para atualizar a lista
                else:
                    st.error(result.get("error", "Erro desconhecido"))
            else:
                st.error(f"Erro ao limpar base: {response.status_code}")
        except Exception as e:
            st.error(f"Erro ao conectar ao servidor: {str(e)}")

# Aba de estatísticas
with tab3:
    st.markdown("## Estatísticas da Base de Conhecimento")

    # Carregar base de conhecimento para estatísticas
    kb = load_knowledge_base()
    documents = kb.get("documents", [])

    if documents:
        # Estatísticas básicas
