      vi.useFakeTimers();
      vi.advanceTimersByTime(30000);

      expect(localStorage.setItem).toHaveBeenCalledWith(
        'emergency_backup',
        expect.stringContaining('test-data')
      );

      vi.useRealTimers();
    });
  });

  describe('health checks', () => {
