        align-items: center;
        justify-content: center;
      }

      .modal-backdrop {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: rgba(0, 0, 0, 0.5);
        backdrop-filter: blur(4px);
      }

      .modal-content {
        position: relative;
        background: white;
        border-radius: 12px;
        box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1);
        padding: 32px;
        max-width: 500px;
        width: 90%;
        max-height: 90vh;
        overflow-y: auto;
      }

      .modal-header {
        display: flex;
        align-items: center;
        justify-content: space-between;
        margin-bottom: 24px;
      }

      .modal-header h2 {
        margin: 0;
        font-size: 24px;
        font-weight: 600;
        color: #1f2937;
      }

      .status-indicator {
        font-size: 12px;
        padding: 4px 12px;
        border-radius: 9999px;
        font-weight: 500;
      }

      .status-indicator.active {
        background: #dbeafe;
        color: #1e40af;
      }

      .status-indicator.paused {
        background: #fef3c7;
        color: #d97706;
      }

      .progress-section {
        margin-bottom: 24px;
      }

      .progress-bar {
        height: 20px;
        background: #f3f4f6;
        border-radius: 10px;
        overflow: hidden;
        position: relative;
        margin-bottom: 12px;
      }

      .progress-fill {
        height: 100%;
        background: linear-gradient(to right, #3b82f6, #2563eb);
        transition: width 0.3s ease;
        display: flex;
        align-items: center;
        justify-content: flex-end;
        padding-right: 8px;
      }

      .progress-text {
        color: white;
        font-size: 12px;
        font-weight: 600;
      }

      .progress-info p {
        margin: 4px 0;
        font-size: 14px;
        color: #6b7280;
      }

      .stats-section {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        gap: 16px;
        margin-bottom: 24px;
        padding: 16px;
        background: #f9fafb;
        border-radius: 8px;
      }

      .stat {
        text-align: center;
      }

      .stat-label {
        display: block;
        font-size: 12px;
        color: #6b7280;
        margin-bottom: 4px;
      }

      .stat-value {
        display: block;
        font-size: 18px;
        font-weight: 600;
        color: #1f2937;
      }

      .stat-value.has-errors {
        color: #ef4444;
      }

      .error-container {
        background: #fef2f2;
        border: 1px solid #fee2e2;
        border-radius: 8px;
        padding: 16px;
        margin-bottom: 24px;
      }

      .error-header {
        display: flex;
        align-items: center;
        gap: 12px;
        margin-bottom: 12px;
      }

      .error-icon {
        width: 20px;
        height: 20px;
        color: #ef4444;
      }

      .error-text {
        margin: 0;
        color: #991b1b;
        font-size: 14px;
      }

      .retry-button {
        background: #ef4444;
        color: white;
        border: none;
        padding: 8px 16px;
        border-radius: 6px;
        font-size: 14px;
        font-weight: 500;
        cursor: pointer;
        transition: background 0.2s;
      }

      .retry-button:hover {
        background: #dc2626;
      }

      .action-buttons {
        display: flex;
        justify-content: center;
        gap: 12px;
      }

      .pause-button,
      .resume-button {
        display: inline-flex;
        align-items: center;
        gap: 8px;
        padding: 10px 20px;
        border: none;
        border-radius: 8px;
        font-size: 16px;
        font-weight: 500;
        cursor: pointer;
        transition: all 0.2s;
      }

      .pause-button {
        background: #f3f4f6;
        color: #4b5563;
      }

      .pause-button:hover {
        background: #e5e7eb;
      }

      .resume-button {
        background: #3b82f6;
        color: white;
      }

      .resume-button:hover {
        background: #2563eb;
      }

      .pause-button svg,
      .resume-button svg {
        width: 20px;
        height: 20px;
      }
    `;
  }
}

