      return {
        usage: 0,
        quota: 0,
        percentUsed: 0,
        availableSpace: 0,
      };
    }

    const estimate = await navigator.storage.estimate();
    const usage = estimate.usage || 0;
    const quota = estimate.quota || 0;
    const percentUsed = quota > 0 ? (usage / quota) : 0;

    return {
      usage,
      quota,
      percentUsed,
      availableSpace: quota - usage,
    };
  }

