  bandwidth: number; // bytes/s (0 = unlimited)
  offline: boolean;
}

export const NetworkProfiles = {
  // Condições normais
  PERFECT: {
    latency: 0,
    jitter: 0,
    packetLoss: 0,
    bandwidth: 0,
    offline: false
  },

  // 3G típico
  SLOW_3G: {
    latency: 400,
    jitter: 100,
    packetLoss: 0.01,
    bandwidth: 50000, // 50KB/s
    offline: false
  },

  // WiFi ruim
  POOR_WIFI: {
    latency: 150,
    jitter: 50,
    packetLoss: 0.05,
    bandwidth: 100000, // 100KB/s
    offline: false
  },

  // Rede congestionada
  CONGESTED: {
    latency: 1000,
    jitter: 500,
    packetLoss: 0.1,
    bandwidth: 10000, // 10KB/s
    offline: false
  },

  // Conexão intermitente
  FLAKY: {
    latency: 200,
