import { defineConfig, splitVendorChunkPlugin } from 'vite';
import react from '@vitejs/plugin-react';
import { visualizer } from 'rollup-plugin-visualizer';
import { compression } from 'vite-plugin-compression2';
import { VitePWA } from 'vite-plugin-pwa';

// https://vitejs.dev/config/
export default defineConfig({
  plugins: [
    react(),
    splitVendorChunkPlugin(),

    // Compressão gzip/brotli
    compression({
      algorithm: 'gzip',
      exclude: [/\.(br)$/, /\.(gz)$/],
    }),
    compression({
      algorithm: 'brotliCompress',
      exclude: [/\.(br)$/, /\.(gz)$/],
    }),

    // Bundle analyzer
    visualizer({
      open: false,
      gzipSize: true,
      brotliSize: true,
      filename: 'dist/stats.html'
    }),

    // PWA
    VitePWA({
      registerType: 'autoUpdate',
      includeAssets: ['favicon.ico', 'robots.txt', 'apple-touch-icon.png'],
      manifest: {
        name: 'Todo RAG System',
        short_name: 'TodoRAG',
        theme_color: '#2196F3',
        icons: [
          {
            src: '/icon-192.png',
            sizes: '192x192',
            type: 'image/png'
          },
          {
            src: '/icon-512.png',
            sizes: '512x512',
            type: 'image/png'
          }
        ]
      }
    })
  ],

  build: {
    target: 'esnext',
    minify: 'terser',
    terserOptions: {
      compress: {
        drop_console: true,
        drop_debugger: true,
        pure_funcs: ['console.log', 'console.info', 'console.debug'],
        passes: 2
      },
      mangle: {
        safari10: true
      },
      format: {
        comments: false
      }
    },

    // Otimização de chunks
    rollupOptions: {
      output: {
        manualChunks: {
          // React ecosystem
          'react-vendor': ['react', 'react-dom', 'react-router-dom'],

          // UI libraries
          'ui-vendor': ['@headlessui/react', '@heroicons/react'],

          // Data/State management
          'data-vendor': ['@tanstack/react-query', 'zustand'],

          // Database
          'db-vendor': ['@electric-sql/pglite', 'drizzle-orm'],

          // Utils
          'utils-vendor': ['date-fns', 'zod'],

          // Heavy libraries (lazy loaded)
          'charts': ['recharts'],
          'editor': ['@monaco-editor/react']
        },

        // Nomes de chunks mais limpos
        chunkFileNames: (chunkInfo) => {
          const facadeModuleId = chunkInfo.facadeModuleId ? chunkInfo.facadeModuleId.split('/').pop() : 'chunk';
          return `js/${facadeModuleId}-[hash].js`;
        },

        assetFileNames: (assetInfo) => {
          const extType = assetInfo.name?.split('.').at(1);
          if (/png|jpe?g|svg|gif|tiff|bmp|ico/i.test(extType || '')) {
            return `images/[name]-[hash][extname]`;
          }
          if (/woff|woff2|eot|ttf|otf/i.test(extType || '')) {
            return `fonts/[name]-[hash][extname]`;
          }
          return `assets/[name]-[hash][extname]`;
        }
      }
    },

    // Limites de chunk
    chunkSizeWarningLimit: 1000, // 1MB

    // Source maps apenas para produção com erro tracking
    sourcemap: process.env.VITE_SOURCE_MAPS === 'true' ? 'hidden' : false,

    // CSS code splitting
    cssCodeSplit: true,

    // Otimizações adicionais
    reportCompressedSize: false, // Mais rápido

    // Assets inline limite
    assetsInlineLimit: 4096 // 4kb
  },

  // Otimizações de desenvolvimento
  server: {
    hmr: {
      overlay: false // Menos intrusivo
    }
  },

  optimizeDeps: {
    include: [
      'react',
      'react-dom',
      '@electric-sql/pglite',
      'drizzle-orm'
    ],
    exclude: [
      '@electric-sql/pglite/vector' // Carregado sob demanda
    ]
  },

  // Resolve aliases
  resolve: {
    alias: {
      '@': '/src',
      '@components': '/src/components',
      '@utils': '/src/utils',
      '@hooks': '/src/hooks',
      '@services': '/src/services',
      '@types': '/src/types'
    }
  }
});

