    category: string,
    metric: string,
    operation: () => T
  ): T {
    const start = performance.now();
    try {
      const result = operation();
      const duration = performance.now() - start;
      this.recordMetric(category, metric, duration);
      return result;
    } catch (error) {
      const duration = performance.now() - start;
      this.recordMetric(category, `${metric}_error`, duration);
      throw error;
    }
  }
}

