import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
import { QueryClient, QueryClientProvider } from '@tanstack/react-query'
import { IssuesListLocal } from './pages/IssuesListLocal'
import { CreateIssueLocal } from './pages/CreateIssueLocal'
import { IssueDetailLocal } from './pages/IssueDetailLocal'
import { ClaudeSessions } from './pages/ClaudeSessions'
import { ClaudeSessionDetailSimple } from './pages/ClaudeSessionDetailSimple'
import { EditIssueLocal } from './pages/EditIssueLocal'
import { Missions } from './pages/Missions'
import { Chat } from './pages/Chat'
import { useEffect, useState } from 'react'
import { swManager } from './service-worker/sw-register'
import { MonitoringWidget } from './monitoring/monitoring-widget'
import { usePrecache } from './hooks/usePrecache'

const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      staleTime: 5 * 60 * 1000,
      gcTime: 10 * 60 * 1000,
      retry: (failureCount, error: any) => {
        if (!navigator.onLine) return false;
        return failureCount < 3;
      }
    }
  }
})

export function App() {
  const [updateAvailable, setUpdateAvailable] = useState(false)
  const { status: precacheStatus } = usePrecache()

  useEffect(() => {
    // Registrar Service Worker
    swManager.register()

    // Escutar atualizações
    const unsubscribe = swManager.onUpdateAvailable((available) => {
      setUpdateAvailable(available)
    })

    return () => {
      unsubscribe()
    }
  }, [])

  const handleUpdate = () => {
    swManager.skipWaiting()
  }

  return (
    <QueryClientProvider client={queryClient}>
      <BrowserRouter>
        <div className="min-h-screen bg-gray-100">
          {/* Banner de atualização */}
          {updateAvailable && (
            <div className="bg-blue-600 text-white p-3 text-center">
              <p className="inline mr-4">Nova versão disponível!</p>
              <button
                onClick={handleUpdate}
                className="bg-white text-blue-600 px-4 py-1 rounded text-sm font-medium hover:bg-blue-50"
              >
                Atualizar Agora
              </button>
            </div>
          )}

          {/* Status de precache */}
          {precacheStatus.isLoading && (
            <div className="bg-gray-800 text-white p-2 text-center text-sm">
              Otimizando para uso offline... {precacheStatus.progress}%
            </div>
          )}

          <nav className="bg-white shadow-sm mb-6">
            <div className="container mx-auto px-4 py-3">
              <div className="flex space-x-6">
                <Link to="/" className="text-blue-600 hover:text-blue-800 font-medium">
                  Potenciais Problemas
                </Link>
                <Link to="/claude-sessions" className="text-blue-600 hover:text-blue-800 font-medium">
                  Claude Sessions
                </Link>
                <Link to="/missions" className="text-blue-600 hover:text-blue-800 font-medium">
                  Missões IA
                </Link>
                <Link to="/chat" className="text-blue-600 hover:text-blue-800 font-medium">
                  Chat
                </Link>
              </div>
            </div>
          </nav>

          <Routes>
            <Route path="/" element={<IssuesListLocal />} />
            <Route path="/issues/new" element={<CreateIssueLocal />} />
            <Route path="/issues/:id" element={<IssueDetailLocal />} />
            <Route path="/issues/:id/edit" element={<EditIssueLocal />} />
            <Route path="/claude-sessions" element={<ClaudeSessions />} />
            <Route path="/claude-sessions/:sessionId" element={<ClaudeSessionDetailSimple />} />
            <Route path="/claude-sessions/:sessionId/:status" element={<ClaudeSessionDetailSimple />} />
            <Route path="/missions" element={<Missions />} />
            <Route path="/chat" element={<Chat />} />
          </Routes>

          {/* Widget de Monitoramento */}
          <MonitoringWidget position="bottom-right" />
        </div>
      </BrowserRouter>
    </QueryClientProvider>
  )
}

