#!/usr/bin/env python3
"""Minimal working MCP server"""

import sys
import json

# Log to stderr for debugging
sys.stderr.write("MCP RAG Server starting...\n")
sys.stderr.flush()

while True:
    try:
        line = sys.stdin.readline()
        if not line:
            break

        # Parse JSON-RPC request
        request = json.loads(line.strip())

        # Log request
        sys.stderr.write(f"Received: {request.get('method', 'unknown')}\n")
        sys.stderr.flush()

        # Handle initialize
        if request.get("method") == "initialize":
            response = {
                "jsonrpc": "2.0",
                "id": request["id"],
                "result": {
                    "protocolVersion": "0.1.0",
                    "capabilities": {
                        "tools": {}
                    },
                    "serverInfo": {
                        "name": "rag-webfetch",
                        "version": "0.1.0"
                    }
                }
            }
            print(json.dumps(response))
            sys.stdout.flush()

        # Handle initialized notification
        elif request.get("method") == "initialized":
            # No response needed for notifications
            pass

        # Handle tools/list
        elif request.get("method") == "tools/list":
            response = {
                "jsonrpc": "2.0",
                "id": request["id"],
                "result": {
                    "tools": [
                        {
                            "name": "rag_test",
                            "description": "Test RAG server",
                            "inputSchema": {
                                "type": "object",
                                "properties": {}
                            }
                        }
                    ]
                }
            }
            print(json.dumps(response))
            sys.stdout.flush()

        # Handle tool calls
        elif request.get("method") == "tools/call":
            response = {
                "jsonrpc": "2.0",
                "id": request["id"],
                "result": {
                    "content": [
                        {
                            "type": "text",
                            "text": "RAG Server is working!"
                        }
                    ]
                }
            }
            print(json.dumps(response))
            sys.stdout.flush()

    except Exception as e:
        sys.stderr.write(f"Error: {e}\n")
        sys.stderr.flush()

