#!/usr/bin/env python3
"""
MCP RAG Server - Versão funcional mínima
"""
import asyncio
import json
import sys
from typing import Any

# Adicionar path do MCP
sys.path.insert(0, '/opt/homebrew/lib/python3.13/site-packages')

try:
    from mcp.server import Server
    from mcp.server.stdio import stdio_server
    from mcp.types import Tool, TextContent
except ImportError as e:
    print(f"Erro ao importar MCP: {e}", file=sys.stderr)
    sys.exit(1)

# Criar servidor
server = Server("rag-webfetch")

# Cache simples
cache = ["Claude Code é uma ferramenta de IA", "MCP permite integração com ferramentas"]

@server.list_tools()
async def handle_list_tools() -> list[Tool]:
    """Lista ferramentas disponíveis"""
    return [
        Tool(
            name="test",
            description="Testa o servidor RAG",
            inputSchema={
                "type": "object",
                "properties": {}
            }
        ),
        Tool(
            name="search",
            description="Busca no cache RAG",
            inputSchema={
                "type": "object",
                "properties": {
                    "query": {"type": "string"}
                },
                "required": ["query"]
            }
        )
    ]

@server.call_tool()
async def handle_call_tool(name: str, arguments: dict) -> list[TextContent]:
    """Processa chamadas de ferramentas"""
    if name == "test":
        return [TextContent(text="Servidor RAG funcionando!")]

    elif name == "search":
        query = arguments.get("query", "")
        results = [item for item in cache if query.lower() in item.lower()]
        if results:
            return [TextContent(text="\n".join(results))]
        else:
            return [TextContent(text=f"Nenhum resultado para '{query}'")]

    else:
        raise ValueError(f"Ferramenta desconhecida: {name}")

async def main():
    """Executa o servidor"""
    async with stdio_server() as (read_stream, write_stream):
        await server.run(
            read_stream,
            write_stream,
            server.create_initialization_options()
        )

if __name__ == "__main__":
    asyncio.run(main())

