import { Hono } from 'hono'
import { z } from 'zod'
import fs from 'fs/promises'
import path from 'path'
import os from 'os'

const app = new Hono()

// Caminho do cache RAG
const RAG_CACHE_DIR = path.join(os.homedir(), '.claude', 'mcp-rag-cache')
const DOCUMENTS_FILE = path.join(RAG_CACHE_DIR, 'documents.json')

/**
 * GET /rag/documents - Lista documentos do cache RAG
 */
app.get('/documents', async (c) => {
  try {
    // Verificar se o arquivo existe
    const exists = await fs.access(DOCUMENTS_FILE).then(() => true).catch(() => false)

    if (!exists) {
      return c.json({
        documents: [],
        stats: {
          total_documents: 0,
          cache_dir: RAG_CACHE_DIR
        }
      })
    }

    // Ler documentos
    const content = await fs.readFile(DOCUMENTS_FILE, 'utf-8')
    const documents = JSON.parse(content)

    // Calcular estatísticas
    const stats = {
      total_documents: documents.length,
      cache_dir: RAG_CACHE_DIR,
      oldest_doc: documents.length > 0 ? documents[0].timestamp : null,
      newest_doc: documents.length > 0 ? documents[documents.length - 1].timestamp : null,
      total_size: content.length
    }

    return c.json({ documents, stats })
  } catch (error) {
    console.error('Erro ao ler cache RAG:', error)
    return c.json({ error: 'Erro ao acessar cache RAG' }, 500)
  }
})

/**
 * POST /rag/search - Busca no cache RAG local
 */
app.post('/search', async (c) => {
  try {
    const body = await c.req.json()
    const { query } = z.object({
      query: z.string(),
      limit: z.number().optional().default(10)
    }).parse(body)

    // Ler documentos
    const content = await fs.readFile(DOCUMENTS_FILE, 'utf-8')
    const documents = JSON.parse(content)

    // Busca simples por palavras-chave
    const queryLower = query.toLowerCase()
    const queryWords = queryLower.split(' ').filter(w => w.length > 2)

    const results = documents
      .map((doc: any) => {
        const contentLower = doc.content.toLowerCase()
        const titleLower = (doc.metadata?.title || '').toLowerCase()
        const sourceLower = doc.source.toLowerCase()

        // Calcular score baseado em matches
        let score = 0
        queryWords.forEach(word => {
          if (contentLower.includes(word)) score += 1
          if (titleLower.includes(word)) score += 2 // Título tem peso maior
          if (sourceLower.includes(word)) score += 0.5
        })

        return {
          ...doc,
          score: score / queryWords.length
        }
      })
      .filter((doc: any) => doc.score > 0)
      .sort((a: any, b: any) => b.score - a.score)
      .slice(0, body.limit || 10)

    return c.json({ results })
  } catch (error) {
    console.error('Erro na busca RAG:', error)
    return c.json({ error: 'Erro ao buscar' }, 500)
  }
})

/**
 * DELETE /rag/document/:id - Remove documento do cache
 */
app.delete('/document/:id', async (c) => {
  try {
    const { id } = c.req.param()

    // Ler documentos atuais
    const content = await fs.readFile(DOCUMENTS_FILE, 'utf-8')
    const documents = JSON.parse(content)

    // Filtrar removendo o documento
    const filtered = documents.filter((doc: any) => doc.id !== id)

    if (filtered.length === documents.length) {
      return c.json({ error: 'Documento não encontrado' }, 404)
    }

    // Salvar de volta
    await fs.writeFile(DOCUMENTS_FILE, JSON.stringify(filtered, null, 2))

    return c.json({ message: 'Documento removido', remaining: filtered.length })
  } catch (error) {
    console.error('Erro ao remover documento:', error)
    return c.json({ error: 'Erro ao remover' }, 500)
  }
})

/**
 * POST /rag/sync - Sincroniza com MCP RAG
 * Útil para atualizar após captura via WebFetch
 */
app.post('/sync', async (c) => {
  try {
    // Aqui você poderia chamar o MCP para sincronizar
    // Por enquanto, apenas retorna o status atual
    const exists = await fs.access(DOCUMENTS_FILE).then(() => true).catch(() => false)

    return c.json({
      synced: exists,
      cache_dir: RAG_CACHE_DIR,
      message: 'Sincronização não implementada ainda'
    })
  } catch (error) {
    return c.json({ error: 'Erro na sincronização' }, 500)
  }
})

export default app

/**
 * Integração do MCP-RAG com o backend do app
 * Permite que o Claude Code use o RAG via comandos MCP
 */

import { exec } from 'child_process'
import { promisify } from 'util'
import { db } from '../db/client'
import { webfetchDocs, webfetchSubpages } from '../db/schema/webfetch-docs'
import { eq } from 'drizzle-orm'

const execAsync = promisify(exec)

export class MCPRAGIntegration {
  private mcpServerPath = '/Users/agents/.claude/mcp-rag-server/server.py'

  /**
   * Dispara indexação de uma URL via MCP
   */
  async indexWebFetchDoc(docId: string) {
    try {
      // Buscar documento no banco
      const [doc] = await db
        .select()
        .from(webfetchDocs)
        .where(eq(webfetchDocs.id, docId))

      if (!doc) {
        throw new Error('Documento não encontrado')
      }

      // Atualizar status para indexing
      await db
        .update(webfetchDocs)
        .set({ status: 'indexing' })
        .where(eq(webfetchDocs.id, docId))

      // Chamar MCP via Python (simulando chamada MCP)
      const command = `python3 -c "
import asyncio
import sys
sys.path.append('/Users/agents/.claude/mcp-rag-server')
from webfetch_integration import WebFetchRAGIntegration
from server import RAGIndex
from pathlib import Path

async def index_url():
    cache_dir = Path.home() / '.claude' / 'mcp-rag-cache'
    rag_index = RAGIndex(cache_dir)
    web_integration = WebFetchRAGIntegration(rag_index)

    result = await web_integration.fetch_and_index_url(
        '${doc.url}',
        max_depth=${doc.maxDepth || 1}
    )

    print(json.dumps(result))

asyncio.run(index_url())
"`

      const { stdout, stderr } = await execAsync(command)

      if (stderr) {
        console.error('Erro MCP:', stderr)
        throw new Error(stderr)
      }

      const result = JSON.parse(stdout)

      if (result.status === 'indexed') {
        // Atualizar documento com sucesso
        await db
          .update(webfetchDocs)
          .set({
            status: 'indexed',
            indexedAt: new Date(),
            documentId: result.doc_id,
            title: result.title || doc.title,
            sections: result.sections || 0
          })
          .where(eq(webfetchDocs.id, docId))

        // Salvar subpáginas indexadas
        if (result.subpages && result.subpages.length > 0) {
          for (const subpage of result.subpages) {
            if (subpage.status === 'indexed') {
              await db.insert(webfetchSubpages).values({
                parentId: docId,
                url: subpage.url,
                title: subpage.title,
                depth: 1,
                status: 'indexed'
              })
            }
          }
        }

        return { success: true, result }
      } else {
        throw new Error(result.error || 'Falha na indexação')
      }

    } catch (error: any) {
      // Atualizar status para failed
      await db
        .update(webfetchDocs)
        .set({ 
          status: 'failed',
          metadata: { error: error.message }
        })
        .where(eq(webfetchDocs.id, docId))

      throw error
    }
  }

  /**
   * Busca no RAG via MCP
   */
  async searchRAG(query: string, mode: string = 'hybrid') {
    try {
      const command = `python3 -c "
import asyncio
import json
import sys
sys.path.append('/Users/agents/.claude/mcp-rag-server')
from server import RAGIndex
from pathlib import Path

async def search():
    cache_dir = Path.home() / '.claude' / 'mcp-rag-cache'
    rag_index = RAGIndex(cache_dir)

    results = rag_index.search('${query}', '${mode}', 10)
    print(json.dumps(results))

asyncio.run(search())
"`

      const { stdout, stderr } = await execAsync(command)

      if (stderr) {
        console.error('Erro na busca:', stderr)
        return []
      }

      return JSON.parse(stdout)

    } catch (error) {
      console.error('Erro ao buscar no RAG:', error)
      return []
    }
  }

  /**
   * Verifica status do MCP-RAG
   */
  async checkHealth() {
    try {
      const command = `python3 -c "
import os
import json
from pathlib import Path

cache_dir = Path.home() / '.claude' / 'mcp-rag-cache'
docs_file = cache_dir / 'documents.json'

if docs_file.exists():
    with open(docs_file, 'r') as f:
        docs = json.load(f)
    print(json.dumps({
        'status': 'healthy',
        'documents': len(docs),
        'cache_dir': str(cache_dir)
    }))
else:
    print(json.dumps({
        'status': 'healthy',
        'documents': 0,
        'cache_dir': str(cache_dir)
    }))
"`

      const { stdout } = await execAsync(command)
      return JSON.parse(stdout)

    } catch (error) {
      return {
        status: 'error',
        error: error.message
      }
    }
  }
}

// Singleton
export const mcpRAG = new MCPRAGIntegration()

// Worker para processar indexações pendentes
export async function processarIndexacoesPendentes() {
  try {
    // Buscar documentos pendentes
    const pendingDocs = await db
      .select()
      .from(webfetchDocs)
      .where(eq(webfetchDocs.status, 'pending'))
      .limit(5) // Processar 5 por vez

    for (const doc of pendingDocs) {
      try {
        console.log(`Indexando: ${doc.url}`)
        await mcpRAG.indexWebFetchDoc(doc.id)
        console.log(`✓ Indexado: ${doc.url}`)
      } catch (error) {
        console.error(`✗ Erro ao indexar ${doc.url}:`, error)
      }

      // Aguardar entre indexações para não sobrecarregar
      await new Promise(resolve => setTimeout(resolve, 2000))
    }

  } catch (error) {
    console.error('Erro ao processar indexações:', error)
  }
}

// Iniciar worker (executar a cada 30 segundos)
if (process.env.NODE_ENV !== 'test') {
  setInterval(processarIndexacoesPendentes, 30000)
  // Executar imediatamente na inicialização
  processarIndexacoesPendentes()
}

import { pgTable, uuid, text, timestamp, jsonb, integer, boolean } from 'drizzle-orm/pg-core'
import { relations } from 'drizzle-orm'

/**
 * Schema para gerenciar documentações capturadas via WebFetch
 */
export const webfetchDocs = pgTable('webfetch_docs', {
  id: uuid('id').primaryKey().defaultRandom(),

  // Informações da URL
  url: text('url').notNull().unique(),
  domain: text('domain').notNull(),
  title: text('title'),
  description: text('description'),

  // Status da indexação
  status: text('status', { 
    enum: ['pending', 'indexing', 'indexed', 'failed', 'archived'] 
  }).default('pending').notNull(),

  // Metadados da captura
  capturedAt: timestamp('captured_at'),
  lastUpdated: timestamp('last_updated'),
  indexedAt: timestamp('indexed_at'),

  // Dados do conteúdo
  contentHash: text('content_hash'), // Para detectar mudanças
  documentId: text('document_id'), // ID no sistema RAG
  sections: integer('sections').default(0),
  words: integer('words').default(0),

  // Configurações
  autoUpdate: boolean('auto_update').default(false),
  updateFrequency: text('update_frequency', {
    enum: ['daily', 'weekly', 'monthly', 'manual']
  }).default('manual'),
  maxDepth: integer('max_depth').default(1),

  // Organização
  category: text('category'), // Ex: "MCP", "Claude", "LangChain"
  tags: jsonb('tags').$type<string[]>().default([]),
  notes: text('notes'),

  // Estatísticas de uso
  searchCount: integer('search_count').default(0),
  lastSearched: timestamp('last_searched'),

  // Metadados extras
  metadata: jsonb('metadata').$type<Record<string, any>>().default({}),

  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull()
})

/**
 * Tabela para rastrear subpáginas indexadas
 */
export const webfetchSubpages = pgTable('webfetch_subpages', {
  id: uuid('id').primaryKey().defaultRandom(),
  parentId: uuid('parent_id').notNull().references(() => webfetchDocs.id, { onDelete: 'cascade' }),

  url: text('url').notNull(),
  title: text('title'),
  depth: integer('depth').notNull(),
  status: text('status', { 
    enum: ['indexed', 'skipped', 'failed'] 
  }).notNull(),

  createdAt: timestamp('created_at').defaultNow().notNull()
})

/**
 * Tabela para histórico de buscas
 */
export const webfetchSearchHistory = pgTable('webfetch_search_history', {
  id: uuid('id').primaryKey().defaultRandom(),

  query: text('query').notNull(),
  mode: text('mode').default('hybrid'),
  resultsCount: integer('results_count').default(0),

  // Documentos que apareceram nos resultados
  matchedDocs: jsonb('matched_docs').$type<string[]>().default([]),

  createdAt: timestamp('created_at').defaultNow().notNull()
})

// Relações
export const webfetchDocsRelations = relations(webfetchDocs, ({ many }) => ({
  subpages: many(webfetchSubpages)
}))

export const webfetchSubpagesRelations = relations(webfetchSubpages, ({ one }) => ({
  parent: one(webfetchDocs, {
    fields: [webfetchSubpages.parentId],
    references: [webfetchDocs.id]
  })
}))

import { Hono } from 'hono'
import { z } from 'zod'
import { db } from '../db/client'
import { webfetchDocs, webfetchSubpages, webfetchSearchHistory } from '../db/schema/webfetch-docs'
import { eq, desc, ilike, and, or, sql } from 'drizzle-orm'
import { mcpRAG } from '../claude/mcp-rag-integration'

const app = new Hono()

// Schemas de validação
const CreateWebFetchDocSchema = z.object({
  url: z.string().url(),
  category: z.string().optional(),
  tags: z.array(z.string()).optional(),
  notes: z.string().optional(),
  autoUpdate: z.boolean().optional(),
  updateFrequency: z.enum(['daily', 'weekly', 'monthly', 'manual']).optional(),
  maxDepth: z.number().min(0).max(3).optional()
})

const UpdateWebFetchDocSchema = CreateWebFetchDocSchema.partial()

const SearchWebFetchSchema = z.object({
  query: z.string(),
  mode: z.enum(['naive', 'local', 'global', 'hybrid']).optional()
})

/**
 * GET /webfetch - Lista documentações
 */
app.get('/', async (c) => {
  const { category, status, search } = c.req.query()

  let conditions = []

  if (category) {
    conditions.push(eq(webfetchDocs.category, category))
  }

  if (status) {
    conditions.push(eq(webfetchDocs.status, status as any))
  }

  if (search) {
    conditions.push(
      or(
        ilike(webfetchDocs.title, `%${search}%`),
        ilike(webfetchDocs.url, `%${search}%`),
        ilike(webfetchDocs.description, `%${search}%`)
      )
    )
  }

  const docs = await db
    .select()
    .from(webfetchDocs)
    .where(conditions.length > 0 ? and(...conditions) : undefined)
    .orderBy(desc(webfetchDocs.createdAt))

  return c.json(docs)
})

/**
 * GET /webfetch/stats - Estatísticas
 */
app.get('/stats', async (c) => {
  const stats = await db
    .select({
      total: sql<number>`count(*)`,
      indexed: sql<number>`count(*) filter (where status = 'indexed')`,
      pending: sql<number>`count(*) filter (where status = 'pending')`,
      failed: sql<number>`count(*) filter (where status = 'failed')`,
      totalSections: sql<number>`sum(sections)`,
      totalWords: sql<number>`sum(words)`,
      categories: sql<string[]>`array_agg(distinct category) filter (where category is not null)`
    })
    .from(webfetchDocs)

  const recentSearches = await db
    .select()
    .from(webfetchSearchHistory)
    .orderBy(desc(webfetchSearchHistory.createdAt))
    .limit(10)

  return c.json({
    ...stats[0],
    recentSearches
  })
})

/**
 * GET /webfetch/:id - Detalhes de uma documentação
 */
app.get('/:id', async (c) => {
  const id = c.req.param('id')

  const [doc] = await db
    .select()
    .from(webfetchDocs)
    .where(eq(webfetchDocs.id, id))

  if (!doc) {
    return c.json({ error: 'Documentação não encontrada' }, 404)
  }

  // Buscar subpáginas
  const subpages = await db
    .select()
    .from(webfetchSubpages)
    .where(eq(webfetchSubpages.parentId, id))
    .orderBy(webfetchSubpages.depth)

  return c.json({
    ...doc,
    subpages
  })
})

/**
 * POST /webfetch - Adiciona nova documentação para indexar
 */
app.post('/', async (c) => {
  const body = await c.req.json()
  const data = CreateWebFetchDocSchema.parse(body)

  // Extrair domínio da URL
  const url = new URL(data.url)
  const domain = url.hostname

  const [doc] = await db
    .insert(webfetchDocs)
    .values({
      ...data,
      domain,
      status: 'pending'
    })
    .returning()

  // Disparar indexação via MCP-RAG
  mcpRAG.indexWebFetchDoc(doc.id).catch(error => {
    console.error('Erro ao disparar indexação:', error)
  })

  return c.json(doc, 201)
})

/**
 * PUT /webfetch/:id - Atualiza documentação
 */
app.put('/:id', async (c) => {
  const id = c.req.param('id')
  const body = await c.req.json()
  const data = UpdateWebFetchDocSchema.parse(body)

  const [updated] = await db
    .update(webfetchDocs)
    .set({
      ...data,
      updatedAt: new Date()
    })
    .where(eq(webfetchDocs.id, id))
    .returning()

  if (!updated) {
    return c.json({ error: 'Documentação não encontrada' }, 404)
  }

  return c.json(updated)
})

/**
 * DELETE /webfetch/:id - Remove documentação
 */
app.delete('/:id', async (c) => {
  const id = c.req.param('id')

  const [deleted] = await db
    .delete(webfetchDocs)
    .where(eq(webfetchDocs.id, id))
    .returning()

  if (!deleted) {
    return c.json({ error: 'Documentação não encontrada' }, 404)
  }

  // TODO: Remover do índice RAG
  // await removeFromIndex(deleted.documentId)

  return c.json({ message: 'Documentação removida com sucesso' })
})

/**
 * POST /webfetch/:id/index - Força reindexação
 */
app.post('/:id/index', async (c) => {
  const id = c.req.param('id')

  const [doc] = await db
    .select()
    .from(webfetchDocs)
    .where(eq(webfetchDocs.id, id))

  if (!doc) {
    return c.json({ error: 'Documentação não encontrada' }, 404)
  }

  // Atualizar status
  await db
    .update(webfetchDocs)
    .set({ status: 'indexing' })
    .where(eq(webfetchDocs.id, id))

  // Disparar indexação via MCP-RAG
  mcpRAG.indexWebFetchDoc(doc.id).catch(error => {
    console.error('Erro ao disparar reindexação:', error)
  })

  return c.json({ message: 'Indexação iniciada' })
})

/**
 * POST /webfetch/search - Busca no RAG
 */
app.post('/search', async (c) => {
  const body = await c.req.json()
  const { query, mode = 'hybrid' } = SearchWebFetchSchema.parse(body)

  // Buscar no MCP-RAG
  const ragResults = await mcpRAG.searchRAG(query, mode)

  // Buscar documentos correspondentes no banco
  const docIds = ragResults
    .filter(r => r.metadata?.url)
    .map(r => r.metadata.url)

  const docs = docIds.length > 0 ? await db
    .select()
    .from(webfetchDocs)
    .where(sql`url = ANY(${docIds})`)
    .limit(10) : []

  // Registrar busca
  await db.insert(webfetchSearchHistory).values({
    query,
    mode,
    resultsCount: docs.length,
    matchedDocs: docs.map(d => d.id)
  })

  // Incrementar contador de buscas
  if (docs.length > 0) {
    await db
      .update(webfetchDocs)
      .set({ 
        searchCount: sql`search_count + 1`,
        lastSearched: new Date()
      })
      .where(sql`id = ANY(${docs.map(d => d.id)})`)
  }

  // Combinar resultados do RAG com metadados do banco
  const enrichedResults = ragResults.map(ragResult => {
    const doc = docs.find(d => d.url === ragResult.metadata?.url)
    return {
      ...ragResult,
      doc: doc || null
    }
  })

  return c.json({
    query,
    mode,
    results: enrichedResults,
    totalResults: ragResults.length
  })
})

/**
 * POST /webfetch/batch - Adiciona múltiplas URLs
 */
app.post('/batch', async (c) => {
  const body = await c.req.json()
  const { urls, category, tags } = z.object({
    urls: z.array(z.string().url()),
    category: z.string().optional(),
    tags: z.array(z.string()).optional()
  }).parse(body)

  const docs = []

  for (const url of urls) {
    const domain = new URL(url).hostname

    const [doc] = await db
      .insert(webfetchDocs)
      .values({
        url,
        domain,
        category,
        tags,
        status: 'pending'
      })
      .onConflictDoNothing()
      .returning()

    if (doc) {
      docs.push(doc)
    }
  }

  return c.json({
    message: `${docs.length} documentações adicionadas`,
    docs
  })
})

export default app

