    params: z.object({
      id: z.coerce.number().int().positive(),
    }),
    response: {
      204: z.null(),
      404: z.object({
        error: z.string(),
      }),
    },
  },
}, async (request, reply) => {
  const { id } = request.params

  // Check if issue exists
  const [existingIssue] = await db
    .select()
    .from(schema.issues)
    .where(eq(schema.issues.id, id))
    .limit(1)

  if (!existingIssue) {
    return reply.status(404).send({ error: 'Issue not found' })
