        data: z.array(z.object({
          id: z.number(),
          name: z.string(),
          createdAt: z.date(),
        })),
        pagination: z.object({
          page: z.number(),
          limit: z.number(),
          total: z.number(),
          totalPages: z.number(),
        }),
      }),
    },
  },
}, async (request, reply) => {
  const { page, limit } = request.query
  const offset = (page - 1) * limit

  const [users, [{ count }]] = await Promise.all([
    db.select()
      .from(schema.users)
      .orderBy(desc(schema.users.createdAt))
      .limit(limit)
      .offset(offset),
    db.select({ count: sql<number>`count(*)::int` })
      .from(schema.users),
  ])

  return {
    data: users,
    pagination: {
      page,
      limit,
      total: count,
      totalPages: Math.ceil(count / limit),
    },
  }
})

app.get('/users/:id', {
  schema: {
    params: z.object({
      id: z.coerce.number().int().positive(),
    }),
    response: {
      200: z.object({
        id: z.number(),
        name: z.string(),
        createdAt: z.date(),
        issuesCount: z.number(),
      }),
      404: z.object({
        error: z.string(),
      }),
    },
  },
}, async (request, reply) => {
  const { id } = request.params

  const [user] = await db
    .select()
    .from(schema.users)
    .where(eq(schema.users.id, id))
    .limit(1)

  if (!user) {
    return reply.status(404).send({ error: 'User not found' })
  }

  // Count user's issues
  const [{ count }] = await db
    .select({ count: sql<number>`count(*)::int` })
    .from(schema.issues)
    .where(eq(schema.issues.userId, id))

  return {
    ...user,
    issuesCount: count,
  }
})

app.get('/health', {
  schema: {
    response: {
      200: z.object({
        status: z.literal('ok'),
        timestamp: z.string(),
        uptime: z.number(),
      }),
    },
  },
}, async () => {
  return {
    status: 'ok' as const,
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
  }
})

// Novo endpoint para tarefas
app.get('/tasks', {
  schema: {
    querystring: z.object({
      page: z.coerce.number().int().positive().default(1),
      limit: z.coerce.number().int().positive().max(100).default(20),
      userId: z.coerce.number().int().positive().optional(),
    }),
    response: {
      200: z.array(z.object({
        content: z.string(),
        status: z.enum(['pending', 'in_progress', 'completed']),
        priority: z.enum(['low', 'medium', 'high']),
        id: z.string(),
      })),
    },
  },
}, async (request, reply) => {
  const { page, limit, userId } = request.query
  const offset = (page - 1) * limit

  const cacheKey = `tasks:${page}:${limit}:${userId || 'all'}`
  const cached = issuesCache.get(cacheKey)

  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    reply.header('X-Cache', 'HIT')
    return cached.data
  }

  const conditions = userId ? eq(schema.issues.userId, userId) : undefined

  const issues = await db.select()
    .from(schema.issues)
    .where(conditions)
    .orderBy(desc(schema.issues.createdAt))
    .limit(limit)
    .offset(offset)

  const tasks = issuesToTasks(issues)

  issuesCache.set(cacheKey, { data: tasks, timestamp: Date.now() })
  reply.header('X-Cache', 'MISS')

  return tasks
})

// Rotas de missões removidas - usando missions-routes.ts

// Registrar rotas do Claude
await app.register(claudeRoutes, { prefix: '/api' })

// Registrar rotas de missões
await app.register(missionsRoutes)

// Registrar rotas de limpeza
await app.register(cleanupRoutes)

// Registrar rotas de documentos
await app.register(documentsRoutes)

// Registrar rotas de WebFetch (Hono para Fastify)
await app.register(async function (fastify) {
  // Adaptador simples para Hono -> Fastify
  fastify.all('/webfetch/*', async (request, reply) => {
    const path = request.url.replace('/api/webfetch', '')
    const method = request.method

    // Criar request compatível com Hono
    const honoRequest = new Request(`http://localhost${path}`, {
      method: method,
      headers: request.headers as any,
      body: method !== 'GET' && method !== 'HEAD' ? JSON.stringify(request.body) : undefined
    })

    // Executar no Hono
    const honoResponse = await webfetchRoutes.fetch(honoRequest)

    // Retornar resposta
    reply.status(honoResponse.status)
    return honoResponse.json()
  })
})

// Registrar rotas de RAG (Hono para Fastify)
await app.register(async function (fastify) {
  fastify.all('/rag/*', async (request, reply) => {
    const path = request.url.replace('/api/rag', '')
    const method = request.method

    const honoRequest = new Request(`http://localhost${path}`, {
      method: method,
      headers: request.headers as any,
      body: method !== 'GET' && method !== 'HEAD' ? JSON.stringify(request.body) : undefined
    })

    const honoResponse = await ragRoutes.fetch(honoRequest)

    reply.status(honoResponse.status)
    return honoResponse.json()
  })
})

app.setErrorHandler((error, _request, reply) => {
  app.log.error(error)

  if (error.validation) {
    return reply.status(400).send({
      error: 'Validation Error',
      message: 'Invalid request data',
      details: error.validation,
    })
  }

  if (error.statusCode === 429) {
    return reply.status(429).send({
      error: 'Too Many Requests',
      message: 'Rate limit exceeded. Please try again later.',
    })
  }

  return reply.status(500).send({
    error: 'Internal Server Error',
    message: 'An unexpected error occurred',
  })
})

const start = async () => {
  try {
    await app.listen({ port: 3333, host: '0.0.0.0' })
    app.log.info('🚀 HTTP server running on http://localhost:3333')
  } catch (err) {
    app.log.error(err)
    process.exit(1)
  }
}

start()

#!/usr/bin/env python3
"""
MCP-RAG Server - Servidor MCP customizado para RAG
Integra com Claude Sessions e oferece busca vetorial real
"""

import json
import os
import sys
import asyncio
from pathlib import Path
from typing import List, Dict, Optional, Any
from datetime import datetime
import hashlib
import numpy as np
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.metrics.pairwise import cosine_similarity
import pickle

# Import WebFetch integration
from webfetch_integration import WebFetchRAGIntegration

# MCP imports
sys.path.append(str(Path(__file__).parent.parent))
from mcp.server import Server
from mcp.server.stdio import stdio_server
from mcp.types import (
    Resource, 
    Tool,
    TextContent,
    CallToolRequest,
    CallToolResult,
    ListResourcesResult,
    ReadResourceRequest,
    ReadResourceResult,
    INTERNAL_ERROR
)

class RAGIndex:
    """Sistema de indexação e busca vetorial"""

    def __init__(self, cache_dir: Path):
        self.cache_dir = cache_dir
        self.cache_dir.mkdir(exist_ok=True)

        # Arquivos de persistência
        self.docs_file = cache_dir / "documents.json"
        self.index_file = cache_dir / "index.pkl"
        self.vectors_file = cache_dir / "vectors.npy"

        # Carregar ou inicializar
        self.documents: List[Dict] = []
        self.vectorizer: Optional[TfidfVectorizer] = None
        self.vectors: Optional[np.ndarray] = None

        self.load()

    def load(self):
        """Carrega índice do disco"""
        if self.docs_file.exists():
            with open(self.docs_file, 'r', encoding='utf-8') as f:
                self.documents = json.load(f)

        if self.index_file.exists() and self.vectors_file.exists():
            with open(self.index_file, 'rb') as f:
                self.vectorizer = pickle.load(f)
            self.vectors = np.load(self.vectors_file)

    def save(self):
        """Salva índice no disco"""
        with open(self.docs_file, 'w', encoding='utf-8') as f:
            json.dump(self.documents, f, ensure_ascii=False, indent=2)

        if self.vectorizer and self.vectors is not None:
            with open(self.index_file, 'wb') as f:
                pickle.dump(self.vectorizer, f)
            np.save(self.vectors_file, self.vectors)

    def add_document(self, content: str, source: str, metadata: Dict = None):
        """Adiciona documento ao índice"""
        doc_id = hashlib.md5(content.encode()).hexdigest()[:8]

        # Evita duplicatas
        for doc in self.documents:
            if doc['id'] == doc_id:
                return doc_id

        document = {
            'id': doc_id,
            'content': content,
            'source': source,
            'metadata': metadata or {},
            'timestamp': datetime.now().isoformat()
        }

        self.documents.append(document)
        self._rebuild_index()
        self.save()

        return doc_id

    def _rebuild_index(self):
        """Reconstrói índice vetorial"""
        if not self.documents:
            return

        texts = [doc['content'] for doc in self.documents]

        # Criar vetorizador TF-IDF
        self.vectorizer = TfidfVectorizer(
            max_features=1000,
            stop_words='english',
            ngram_range=(1, 2)
        )

        self.vectors = self.vectorizer.fit_transform(texts).toarray()

    def search(self, query: str, mode: str = 'hybrid', limit: int = 5) -> List[Dict]:
        """Busca documentos relevantes"""
        if not self.documents or self.vectorizer is None:
            return []

        # Vetorizar query
        query_vector = self.vectorizer.transform([query]).toarray()

        # Calcular similaridade
        similarities = cosine_similarity(query_vector, self.vectors)[0]

        # Ordenar por relevância
        indices = np.argsort(similarities)[::-1][:limit]

        results = []
        for idx in indices:
            if similarities[idx] > 0.1:  # Threshold mínimo
                doc = self.documents[idx].copy()
                doc['score'] = float(similarities[idx])
                results.append(doc)

        return results

class ClaudeSessionsIntegration:
    """Integração com Claude Sessions"""

    def __init__(self, claude_base: Path):
        self.claude_base = claude_base
        self.todos_dir = claude_base / "todos"
        self.projects_dir = claude_base / "projects"

    def get_all_sessions(self) -> List[Dict]:
        """Lista todas as sessões"""
        sessions = []

        if not self.todos_dir.exists():
            return sessions

        for todo_file in self.todos_dir.glob("*.json"):
            try:
                session_id = todo_file.stem
                with open(todo_file, 'r', encoding='utf-8') as f:
                    todos = json.load(f)

                sessions.append({
                    'session_id': session_id,
                    'todos': todos,
                    'todo_count': len(todos),
                    'file': str(todo_file)
                })
            except Exception as e:
                print(f"Erro ao ler {todo_file}: {e}")

        return sessions

    def get_session_content(self, session_id: str) -> str:
        """Extrai conteúdo textual de uma sessão"""
        content_parts = [f"Sessão Claude: {session_id}\n"]

        # Ler todos
        todo_file = self.todos_dir / f"{session_id}.json"
        if todo_file.exists():
            with open(todo_file, 'r', encoding='utf-8') as f:
                todos = json.load(f)

            if todos:
                content_parts.append("\nTAREFAS:")
                for i, todo in enumerate(todos, 1):
                    status = todo.get('status', 'pending')
                    content = todo.get('content', '')
                    priority = todo.get('priority', 'medium')
                    content_parts.append(f"{i}. [{status}] {content} (Prioridade: {priority})")

        # Buscar conversa
        for project_dir in self.projects_dir.glob("*"):
            conv_file = project_dir / f"{session_id}.jsonl"
            if conv_file.exists():
                content_parts.append("\n\nCONVERSA:")
                with open(conv_file, 'r', encoding='utf-8') as f:
                    lines = f.readlines()[:20]  # Primeiras 20 linhas

                for line in lines:
                    try:
                        entry = json.loads(line)
                        if entry.get('type') == 'user' and 'message' in entry:
                            msg = entry['message'].get('content', '')[:200]
                            content_parts.append(f"User: {msg}...")
                    except:
                        pass
                break

        return "\n".join(content_parts)

# Instância global do servidor
app = Server("mcp-rag-server")
rag_index: Optional[RAGIndex] = None
claude_integration: Optional[ClaudeSessionsIntegration] = None
web_integration: Optional[WebFetchRAGIntegration] = None

@app.list_resources()
async def list_resources() -> ListResourcesResult:
    """Lista recursos disponíveis"""
    resources = [
        Resource(
            uri="rag://stats",
            name="Estatísticas do RAG",
            description="Mostra estatísticas do índice RAG",
            mimeType="application/json"
        ),
        Resource(
            uri="rag://sessions",
            name="Claude Sessions",
            description="Lista todas as sessões do Claude",
            mimeType="application/json"
        )
    ]

    # Adicionar documentos indexados como recursos
    if rag_index:
        for doc in rag_index.documents[:10]:  # Limitar a 10
            resources.append(Resource(
                uri=f"rag://doc/{doc['id']}",
                name=f"Documento {doc['source']}",
                description=doc['content'][:100] + "...",
                mimeType="text/plain"
            ))

    return ListResourcesResult(resources=resources)

@app.read_resource()
async def read_resource(request: ReadResourceRequest) -> ReadResourceResult:
    """Lê um recurso específico"""
    uri = request.uri

    if uri == "rag://stats":
        stats = {
            "total_documents": len(rag_index.documents) if rag_index else 0,
            "index_size": len(rag_index.vectors) if rag_index and rag_index.vectors is not None else 0,
            "cache_dir": str(rag_index.cache_dir) if rag_index else None
        }
        return ReadResourceResult(
            contents=[TextContent(
                text=json.dumps(stats, indent=2),
                uri=uri,
                mimeType="application/json"
            )]
        )

    elif uri == "rag://sessions":
        sessions = claude_integration.get_all_sessions() if claude_integration else []
        return ReadResourceResult(
            contents=[TextContent(
                text=json.dumps(sessions, indent=2),
                uri=uri,
                mimeType="application/json"
            )]
        )

    elif uri.startswith("rag://doc/"):
        doc_id = uri.replace("rag://doc/", "")
        if rag_index:
            for doc in rag_index.documents:
                if doc['id'] == doc_id:
                    return ReadResourceResult(
                        contents=[TextContent(
                            text=doc['content'],
                            uri=uri,
                            mimeType="text/plain"
                        )]
                    )

    raise ValueError(f"Recurso não encontrado: {uri}")

@app.list_tools()
async def list_tools() -> List[Tool]:
    """Lista ferramentas disponíveis"""
    return [
        Tool(
            name="rag_search",
            description="Busca informações no índice RAG",
            inputSchema={
                "type": "object",
                "properties": {
                    "query": {
                        "type": "string",
