/**
 * Rotas para integra√ß√£o com Claude Sessions
 */

import { FastifyInstance } from 'fastify'
import { z } from 'zod'
import { ClaudeIntegration } from '../claude/integration'

const claude = new ClaudeIntegration()

export async function claudeRoutes(app: FastifyInstance) {
  // Listar todas as sess√µes
  app.get('/claude-sessions', async (request, reply) => {
    try {
      const sessions = await claude.getAllSessions()

      return reply.send({
        success: true,
        count: sessions.length,
        sessions
      })
    } catch (error) {
      console.error('Erro ao buscar sess√µes:', error)
      return reply.status(500).send({
        success: false,
        error: 'Erro ao buscar sess√µes'
      })
    }
  })

  // Buscar detalhes de uma sess√£o
  app.get('/claude-sessions/:sessionId', {
    schema: {
      params: z.object({
        sessionId: z.string()
      })
    }
  }, async (request, reply) => {
    const { sessionId } = request.params

    try {
      const session = await claude.getSessionDetails(sessionId)

      if (!session) {
        return reply.status(404).send({
          success: false,
          error: 'Sess√£o n√£o encontrada'
        })
      }

      return reply.send({
        success: true,
        session
      })
    } catch (error) {
      console.error('Erro ao buscar sess√£o:', error)
      return reply.status(500).send({
        success: false,
        error: 'Erro ao buscar detalhes da sess√£o'
      })
    }
  })

  // Buscar apenas todos de uma sess√£o
  app.get('/claude-sessions/:sessionId/todos', {
    schema: {
      params: z.object({
        sessionId: z.string()
      })
    }
  }, async (request, reply) => {
    const { sessionId } = request.params

    try {
      const session = await claude.getSessionDetails(sessionId)

      if (!session) {
        return reply.status(404).send({
          success: false,
          error: 'Sess√£o n√£o encontrada'
        })
      }

      const todos = session.todos
      const stats = {
        total: todos.length,
        pending: todos.filter((t: any) => t.status === 'pending').length,
        inProgress: todos.filter((t: any) => t.status === 'in_progress').length,
        completed: todos.filter((t: any) => t.status === 'completed').length
      }

      return reply.send({
        success: true,
        sessionId,
        todos,
        stats
      })
    } catch (error) {
      console.error('Erro ao buscar todos:', error)
      return reply.status(500).send({
        success: false,
        error: 'Erro ao buscar todos da sess√£o'
      })
    }
  })

  // Buscar apenas sess√µes ativas (com todos pendentes)
  app.get('/claude-sessions/active', async (request, reply) => {
    try {
      const sessions = await claude.getAllSessions()
      const activeSessions = sessions.filter(s => s.pendingCount > 0)

      return reply.send({
        success: true,
        count: activeSessions.length,
        sessions: activeSessions
      })
    } catch (error) {
      console.error('Erro ao buscar sess√µes ativas:', error)
      return reply.status(500).send({
        success: false,
        error: 'Erro ao buscar sess√µes ativas'
      })
    }
  })

  // Sincronizar todos de uma sess√£o com o banco local
  app.post('/claude-sessions/:sessionId/sync', {
    schema: {
      params: z.object({
        sessionId: z.string()
      })
    }
  }, async (request, reply) => {
    const { sessionId } = request.params

    try {
      const session = await claude.getSessionDetails(sessionId)

      if (!session) {
        return reply.status(404).send({
          success: false,
          error: 'Sess√£o n√£o encontrada'
        })
      }

      await claude.syncTodosToDatabase(sessionId, session.todos)

      return reply.send({
        success: true,
        message: `${session.todos.length} todos sincronizados`
      })
    } catch (error) {
      console.error('Erro ao sincronizar todos:', error)
      return reply.status(500).send({
        success: false,
        error: 'Erro ao sincronizar todos'
      })
    }
  })
}

