          page: z.number(),
          limit: z.number(),
          total: z.number(),
          totalPages: z.number(),
        }),
      }),
    },
  },
}, async (request, reply) => {
  const { page, limit } = request.query
  const offset = (page - 1) * limit

  const [users, [{ count }]] = await Promise.all([
    db.select()
      .from(schema.users)
      .orderBy(desc(schema.users.createdAt))
      .limit(limit)
      .offset(offset),
    db.select({ count: sql<number>`count(*)::int` })
      .from(schema.users),
  ])

  return {
    data: users,
    pagination: {
      page,
      limit,
      total: count,
      totalPages: Math.ceil(count / limit),
    },
  }
})

app.get('/users/:id', {
  schema: {
    params: z.object({
      id: z.coerce.number().int().positive(),
    }),
    response: {
      200: z.object({
        id: z.number(),
        name: z.string(),
        createdAt: z.date(),
        issuesCount: z.number(),
      }),
      404: z.object({
        error: z.string(),
      }),
    },
  },
}, async (request, reply) => {
  const { id } = request.params

  const [user] = await db
    .select()
    .from(schema.users)
    .where(eq(schema.users.id, id))
    .limit(1)

  if (!user) {
    return reply.status(404).send({ error: 'User not found' })
  }

  // Count user's issues
  const [{ count }] = await db
    .select({ count: sql<number>`count(*)::int` })
