import { fastifyCors } from "@fastify/cors";
import { fastifyCompress } from "@fastify/compress";
import { fastifyEtag } from "@fastify/etag";
import { fastifyHelmet } from "@fastify/helmet";
import { serializerCompiler, validatorCompiler, type ZodTypeProvider } from 'fastify-type-provider-zod'
import { fastify } from "fastify";
import { z } from "zod";
import { db } from "../db/client.js";
import { schema } from "../db/schema/index.js";
import { eq, desc, sql } from "drizzle-orm";
import { issuesToTasks } from "../utils/issue-to-task-transformer.js";
import { claudeRoutes } from "./claude-routes.js";

const app = fastify({ 
  logger: {
    level: 'info',
    transport: {
      target: 'pino-pretty',
      options: {
        translateTime: 'HH:MM:ss Z',
        ignore: 'pid,hostname',
      },
    },
  },
}).withTypeProvider<ZodTypeProvider>()

app.setSerializerCompiler(serializerCompiler)
app.setValidatorCompiler(validatorCompiler)

await app.register(fastifyHelmet)
await app.register(fastifyCompress, { global: true })
await app.register(fastifyEtag)
await app.register(fastifyCors, {
  origin: process.env.NODE_ENV === 'production' ? 'https://yourdomain.com' : true,
  credentials: true,
})

const issuesCache = new Map<string, { data: any; timestamp: number }>()
const CACHE_TTL = 60 * 1000 // 1 minute

app.post('/issues', {
  schema: {
    body: z.object({
      title: z.string().min(1).max(255),
      description: z.string().optional(),
      userId: z.coerce.number().int().positive(),
    }),
    response: {
      201: z.object({
        id: z.number(),
        title: z.string(),
        userId: z.number(),
        createdAt: z.date(),
      }),
    },
  },
}, async (request, reply) => {
  const { title, description, userId } = request.body

  const [newIssue] = await db.insert(schema.issues).values({
    title,
    description,
    userId,
  }).returning()

  issuesCache.clear()

  return reply.status(201).send(newIssue)
})

app.get('/issues', {
  schema: {
    querystring: z.object({
      page: z.coerce.number().int().positive().default(1),
      limit: z.coerce.number().int().positive().max(100).default(20),
      userId: z.coerce.number().int().positive().optional(),
    }),
    response: {
      200: z.object({
        data: z.array(z.object({
          id: z.number(),
          title: z.string(),
          description: z.string().nullable(),
          userId: z.number(),
          createdAt: z.date(),
        })),
        pagination: z.object({
          page: z.number(),
          limit: z.number(),
          total: z.number(),
          totalPages: z.number(),
        }),
      }),
    },
  },
}, async (request, reply) => {
  const { page, limit, userId } = request.query
  const offset = (page - 1) * limit

  const cacheKey = `issues:${page}:${limit}:${userId || 'all'}`
  const cached = issuesCache.get(cacheKey)

  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    reply.header('X-Cache', 'HIT')
    return cached.data
  }

  const conditions = userId ? eq(schema.issues.userId, userId) : undefined

  const [issues, [{ count }]] = await Promise.all([
    db.select()
      .from(schema.issues)
      .where(conditions)
      .orderBy(desc(schema.issues.createdAt))
      .limit(limit)
      .offset(offset),
    db.select({ count: sql<number>`count(*)::int` })
      .from(schema.issues)
      .where(conditions),
  ])

  const result = {
    data: issues,
    pagination: {
      page,
      limit,
      total: count,
      totalPages: Math.ceil(count / limit),
    },
  }

  issuesCache.set(cacheKey, { data: result, timestamp: Date.now() })
  reply.header('X-Cache', 'MISS')

  return result
})

