SCRIPT_DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" &> /dev/null && pwd )"
BASE_DIR="$SCRIPT_DIR"
cd "$BASE_DIR"

# Definir PATH para incluir Node.js
export PATH="$PATH:/opt/homebrew/bin:/usr/local/bin"

