echo "Verificando se porta 8020 já está em uso..."
if netstat -an | grep -q "127.0.0.1.8020" || lsof -i:8020 &>/dev/null; then
    echo "Porta 8020 já está em uso!"
    echo "Tentando encontrar e encerrar o processo..."

    # Encontrar PIDs usando a porta
    PIDS=$(lsof -i:8020 -t 2>/dev/null)
    if [ -n "$PIDS" ]; then
        echo "Encerrando processos: $PIDS"
        kill -15 $PIDS
        sleep 2
    fi
fi

# Encerrar qualquer processo LightRAG existente
echo "Encerrando quaisquer processos LightRAG existentes..."
pkill -f "lightrag_server|lightrag.server|server-lightrag" || true

# Aguardar encerramento
sleep 2

# Verificar se node está disponível
if ! command -v node &> /dev/null; then
    echo "ERRO: Node.js não encontrado no PATH!"
    echo "Tentando localizar Node.js..."
    NODE_PATHS="/opt/homebrew/bin/node /usr/local/bin/node /usr/bin/node"
