        if [ -x "$cmd" ]; then
            NODE_CMD="$cmd"
            echo "Node.js encontrado em: $NODE_CMD"
            break
        fi
    done

    if [ -z "$NODE_CMD" ]; then
        echo "Node.js não encontrado. Abortando."
        exit 1
    fi
else
    NODE_CMD="node"
    echo "Usando Node.js: $(which node) $(node -v)"
fi

# Iniciar o servidor
echo "Iniciando servidor LightRAG..."
$NODE_CMD "$BASE_DIR/lightrag_server.js" > "$BASE_DIR/logs/lightrag.log" 2>&1 &
SERVER_PID=$!

# Verificar se o servidor iniciou
sleep 3
if kill -0 $SERVER_PID 2>/dev/null; then
    echo "Servidor LightRAG iniciado com sucesso! (PID: $SERVER_PID)"
