const server = http.createServer((req, res) => {
    // Configurar CORS
    res.setHeader('Access-Control-Allow-Origin', '*');
    res.setHeader('Access-Control-Allow-Methods', 'GET, POST, OPTIONS');
    res.setHeader('Access-Control-Allow-Headers', 'Content-Type');

    // Preflight OPTIONS
    if (req.method === 'OPTIONS') {
        res.statusCode = 204;
        res.end();
        return;
    }

    // Extrair corpo da requisição
    let body = '';
    req.on('data', chunk => {
        body += chunk.toString();
    });

    req.on('end', () => {
        console.log(`${new Date().toISOString()} - ${req.method} ${req.url}`);

        try {
            // Rotas da API
            if (req.method === 'POST' && req.url === '/query') {
                // Processar consulta
                const data = JSON.parse(body);
                console.log('Consulta recebida:', data.query);

                // Resposta para consulta
                const response = {
                    response: `Resposta para: "${data.query}"`,
                    context: []
                };

                // Adicionar contextos da base de conhecimento se existirem
                if (knowledgeBase.documents.length > 0) {
                    // Simulação de busca (simplificada)
                    const query = data.query.toLowerCase();
                    const relevantDocs = knowledgeBase.documents
                        .filter(doc => doc.content.toLowerCase().includes(query))
                        .slice(0, 3);

                    if (relevantDocs.length > 0) {
                        response.context = relevantDocs.map(doc => ({
                            content: doc.content.substring(0, 200) + '...',
                            source: doc.id || 'documento'
                        }));

                        response.response = `Com base no conhecimento disponível, aqui está a resposta para: "${data.query}"`;
                    }
                }

                res.statusCode = 200;
                res.setHeader('Content-Type', 'application/json');
                res.end(JSON.stringify(response));

            } else if (req.method === 'POST' && req.url === '/insert') {
                // Processar inserção de texto
                const data = JSON.parse(body);

                if (data.text) {
                    console.log('Inserção recebida:', data.text.substring(0, 50) + '...');

                    // Adicionar à base de conhecimento
                    knowledgeBase.documents.push({
                        id: 'doc_' + Date.now().toString(),
                        content: data.text,
                        created: new Date().toISOString()
                    });

                    knowledgeBase.lastUpdated = new Date().toISOString();
                    saveKnowledgeBase();

                    res.statusCode = 200;
                    res.setHeader('Content-Type', 'application/json');
                    res.end(JSON.stringify({ 
                        success: true, 
                        message: 'Documento inserido com sucesso'
                    }));
                } else {
                    res.statusCode = 400;
                    res.setHeader('Content-Type', 'application/json');
                    res.end(JSON.stringify({ 
                        success: false, 
                        error: 'Texto para inserção não fornecido'
                    }));
                }
            } else {
                // Rota não encontrada
                res.statusCode = 404;
                res.setHeader('Content-Type', 'application/json');
                res.end(JSON.stringify({ 
                    success: false, 
                    error: 'Rota não encontrada' 
                }));
            }
        } catch (err) {
            console.error('Erro ao processar requisição:', err);
            res.statusCode = 500;
            res.setHeader('Content-Type', 'application/json');
            res.end(JSON.stringify({ 
                success: false, 
                error: `Erro no servidor: ${err.message}`
            }));
        }
    });
});

// Iniciar servidor
server.listen(PORT, HOST, () => {
    console.log(`Servidor LightRAG simplificado rodando em http://${HOST}:${PORT}`);
    console.log('Este servidor é uma implementação básica para fins de demonstração');
    console.log('Pronto para receber consultas em /query e inserções em /insert');
